import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, TextField } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

import SingleOptionSelector from "./SingleOptionSelector";
import ToppingsSelector from "./ToppingsSelector";

// ID → Label mappings (you can replace with actual options from DB)
const OPTION_LABELS = {
  size: { 5: "Small", 6: "Medium", 7: "Large" },
  flavor: { 8: "Chocolate", 9: "Vanilla", 10: "Strawberry" },
  shape: { 2: "Round", 3: "Heart", 8: "Square" },
  toppings: {
    12: "Choco Chips",
    14: "Rainbow Sprinkles",
    18: "Nuts",
    20: "Fruits",
    21: "Cherries"
  }
};

// Safely convert backend values to array
const toArray = (value) => {
  if (!value) return [];
  if (Array.isArray(value)) return value;
  if (typeof value === "number") return [value];
  if (typeof value === "string") {
    try {
      const parsed = JSON.parse(value);
      if (Array.isArray(parsed)) return parsed;
      return [parsed];
    } catch {
      return value.split(",").map(Number).filter(Boolean);
    }
  }
  return [];
};

// Safely normalize selectedSingle
const normalizeSelectedSingle = (selectedSingle) => {
  if (!selectedSingle) return {};
  if (typeof selectedSingle === "string") {
    try {
      return JSON.parse(selectedSingle);
    } catch {
      return {};
    }
  }
  if (Array.isArray(selectedSingle)) {
    return selectedSingle.reduce((acc, cur) => {
      acc[cur.type] = cur.id;
      return acc;
    }, {});
  }
  return selectedSingle; // assume object
};

export default function CartPage() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  // Fetch cart items
  useEffect(() => {
    async function fetchData() {
      const response = await getCart();
      setItems(response.data);
    }
    fetchData();
  }, []);

  // Remove item
  const handleRemove = async (id) => {
    await removeFromCart(id);
    const response = await getCart();
    setItems(response.data);
  };

  // Update quantity
  const handleQty = async (id, qty) => {
    if (qty < 1) return;
    await updateCartItem(id, { quantity: qty });
    const response = await getCart();
    setItems(response.data);
  };

  // Total price
  const total = items.reduce(
    (s, it) => s + (it.totalPrice ?? it.basePrice * it.quantity),
    0
  );

  if (!items.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Box display="grid" gap={2}>
        {items.map((it) => {
          const selected = normalizeSelectedSingle(it.selectedSingle);
          const toppingsArray = toArray(it.selectedToppings);

          return (
            <Paper key={it.id} sx={{ display: "flex", p: 2 }}>
              {/* Image */}
              <img
                src={`/images/${it.image}`}
                alt={it.name}
                style={{ width: 120, borderRadius: 8, marginRight: 16 }}
              />

              <Box sx={{ flex: 1 }}>
                <Typography variant="h6">{it.name}</Typography>

                <Typography variant="body2">
                  <b>Size:</b>{" "}
                  {OPTION_LABELS.size[selected.size] ?? "None"}
                </Typography>

                <Typography variant="body2">
                  <b>Flavor:</b>{" "}
                  {OPTION_LABELS.flavor[selected.flavor] ?? "None"}
                </Typography>

                <Typography variant="body2">
                  <b>Shape:</b>{" "}
                  {OPTION_LABELS.shape[selected.shape] ?? "None"}
                </Typography>

                <Typography variant="body2">
                  <b>Toppings:</b>{" "}
                  {toppingsArray.length
                    ? toppingsArray
                        .map((id) => OPTION_LABELS.toppings[id] ?? id)
                        .join(", ")
                    : "None"}
                </Typography>
              </Box>

              <Box sx={{ textAlign: "right" }}>
                <TextField
                  type="number"
                  value={it.quantity}
                  onChange={(e) =>
                    handleQty(it.id, Math.max(1, parseInt(e.target.value || "1")))
                  }
                  sx={{ width: 80, mb: 1 }}
                  InputProps={{ inputProps: { min: 1 } }}
                />

                <Typography>
                  ₹{it.totalPrice ?? it.basePrice * it.quantity}
                </Typography>

                <Button color="error" onClick={() => handleRemove(it.id)}>
                  Remove
                </Button>
              </Box>
            </Paper>
          );
        })}
      </Box>

      <Typography variant="h5" sx={{ mt: 3 }}>
        Total: ₹{total}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 2 }}
        onClick={() => navigate("/checkout")}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );
}
