// src/pages/RegisterPage.js
import React, { useState } from "react";
import { Box, TextField, Button, Typography, Paper, MenuItem } from "@mui/material";
import { useNavigate } from "react-router-dom";

export default function RegisterPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [role, setRole] = useState("user"); // user or admin
  const [error, setError] = useState("");

  const handleRegister = () => {
    if (!username) {
      setError("Username is required");
      return;
    }

    // Save user info locally (simple approach)
    localStorage.setItem("tempUsername", username); // optional, just for demo
    localStorage.setItem("tempRole", role);

    alert("Registration successful! Please login.");
    navigate("/login"); // redirect to login page
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        bgcolor: "#f5f5f5",
      }}
    >
      <Paper sx={{ p: 4, width: 400 }}>
        <Typography variant="h5" sx={{ mb: 3, textAlign: "center" }}>
          Register
        </Typography>

        <TextField
          label="Username"
          fullWidth
          sx={{ mb: 2 }}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <TextField
          select
          label="Role"
          fullWidth
          sx={{ mb: 2 }}
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <MenuItem value="user">User</MenuItem>
          <MenuItem value="admin">Admin</MenuItem>
        </TextField>

        {error && (
          <Typography color="error" sx={{ mb: 2 }}>
            {error}
          </Typography>
        )}

        <Button variant="contained" fullWidth onClick={handleRegister}>
          Register
        </Button>

        <Typography
          sx={{ mt: 2, textAlign: "center", cursor: "pointer", color: "primary.main" }}
          onClick={() => navigate("/login")}
        >
          Already have an account? Login
        </Typography>
      </Paper>
    </Box>
  );
}
