// src/pages/CustomizerPage.js
import React, { useEffect, useState } from "react";
import { Box, Typography, CircularProgress } from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

import SingleOptionSelector from "../components/SingleOptionSelector";
import ToppingsSelector from "../components/ToppingsSelector";
import PriceBox from "../components/PriceBox";

const CustomizerPage = () => {
  const { productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  const [selectedSingle, setSelectedSingle] = useState("");
  const [selectedToppings, setSelectedToppings] = useState([]);
  const [price, setPrice] = useState(0);

  useEffect(() => {
    axios.get(`http://localhost:8080/api/products/${productId}`)
      .then(res => setProduct(res.data))
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, [productId]);

  useEffect(() => {
    if (!product) return;
    let total = product.basePrice;
    if (selectedSingle) {
      const singleOption = product.singleOptions.find(opt => opt.name === selectedSingle);
      total += singleOption ? singleOption.price : 0;
    }
    selectedToppings.forEach(t => {
      const topping = product.toppings.find(opt => opt.name === t);
      if (topping) total += topping.price;
    });
    setPrice(total);
  }, [selectedSingle, selectedToppings, product]);

  const handleAddToCart = () => {
    const payload = {
      productId: product.id,
      selectedSingle,
      selectedToppings,
      quantity: 1
    };
    axios.post("http://localhost:8080/api/cart/add", payload)
      .then(() => navigate("/cart"))
      .catch(err => console.error(err));
  };

  if (loading) return <Box sx={{ textAlign: "center", mt: 5 }}><CircularProgress /></Box>;
  if (!product) return <Typography sx={{ p: 3 }}>Product not found.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>{product.name}</Typography>
      <img src={product.image} alt={product.name} style={{ width: "100%", maxWidth: "400px", borderRadius: "10px", marginBottom: "20px" }} />
      <Typography variant="body1" sx={{ mb: 3 }}>{product.description}</Typography>

      <SingleOptionSelector
        options={product.singleOptions}
        selectedOption={selectedSingle}
        onChange={setSelectedSingle}
      />

      <ToppingsSelector
        options={product.toppings}
        selectedToppings={selectedToppings}
        onChange={setSelectedToppings}
      />

      <PriceBox price={price} onAddToCart={handleAddToCart} />
    </Box>
  );
};

export default CustomizerPage;
