import React from "react";
import { Box, FormControl, FormLabel, RadioGroup, FormControlLabel, Radio } from "@mui/material";

export default function SingleOptionSelector({ title, options = [], selected, onChange }) {
  return (
    <Box sx={{ mb: 2 }}>
      <FormControl component="fieldset">
        <FormLabel component="legend" sx={{ textTransform: "capitalize" }}>{title}</FormLabel>
        <RadioGroup value={selected || ""} onChange={(e) => onChange(e.target.value)}>
          {options.map(opt => (
            <FormControlLabel
              key={opt.id || opt.value || opt.name}
              value={opt.value ?? opt.name}
              control={<Radio />}
              label={`${opt.value ?? opt.name} ${opt.extraPrice ? `(₹${opt.extraPrice})` : ""}`}
            />
          ))}
        </RadioGroup>
      </FormControl>
    </Box>
  );
}
