import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getProductById } from "../api/products";
import { getAllOptions } from "../api/options";
import { calculatePrice } from "../api/price";
import { addToCart } from "../api/cart";

export default function CustomizerPage() {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    getProductById(productId).then(res => setProduct(res.data));
    getAllOptions().then(res => setOptions(res.data));
  }, [productId]);

  useEffect(() => {
    if (product) {
      calculatePrice({ productId, selectedOptions }).then(res => setTotalPrice(res.data.total));
    }
  }, [selectedOptions, product]);

  const handleOptionChange = (type, value) => {
    setSelectedOptions(prev => ({ ...prev, [type]: value }));
  };

  const handleAddToCart = () => {
    addToCart({ productId, options: selectedOptions, quantity: 1 }).then(() => {
      alert("Added to cart!");
      navigate("/cart");
    });
  };

  if (!product) return <p>Loading...</p>;

  return (
    <div style={{ padding: "40px" }}>
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} style={{ width: "300px" }} />
      <div style={{ marginTop: "20px" }}>
        {options.map(opt => (
          <div key={opt.id}>
            <label>{opt.name}: </label>
            <select onChange={(e) => handleOptionChange(opt.type, e.target.value)}>
              <option value="">Select {opt.name}</option>
              {opt.values.map(val => (
                <option key={val} value={val}>{val}</option>
              ))}
            </select>
          </div>
        ))}
      </div>
      <h3>Total Price: â‚¹{totalPrice}</h3>
      <button onClick={handleAddToCart}>Add to Cart</button>
    </div>
  );
}
