    Button, 
    CircularProgress,
    Paper, // Added Paper for section elevation
    Grid, // Added Grid for two-column layout
    Stack // Added Stack for consistent vertical spacing
} from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
