¬† ¬† ¬† ¬† setOpts({
¬† ¬† ¬† ¬† ¬† topping: tRes.data,
¬† ¬† ¬† ¬† ¬† size: sizeRes.data,
¬† ¬† ¬† ¬† ¬† flavor: flavorRes.data,
¬† ¬† ¬† ¬† ¬† shape: shapeRes.data,
¬† ¬† ¬† ¬† ¬† decoration: decoRes.data,
¬† ¬† ¬† ¬† });
¬† ¬† ¬† })
