// src/components/Categories.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Card, CardActionArea, CardMedia, CardContent } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { getAllCategories } from "../api/categories";
import getCategoryImage from "../utils/categoryImageMap";

export default function Categories() {
  const [list, setList] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getAllCategories()
      .then(res => setList(res.data))
      .catch(console.error);
  }, []);

  return (
    <Box sx={{ mt: 3 }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Shop by category</Typography>
      <Grid container spacing={2}>
        {list.map(cat => (
          <Grid item key={cat.id} xs={177} sm={6} md={3}>
            <Card>
              <CardActionArea onClick={() => navigate(`/category/${cat.id}`)}>
                <CardMedia component="img" height="140" image={getCategoryImage(cat)} alt={cat.name} />
                <CardContent>
                  <Typography variant="subtitle1" align="center">{cat.name}</Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
