import React, { useState } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

export function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async () => {
    if (!username || !password) {
      alert("Please enter username and password");
      return;
    }

    try {
      const res = await axios.post("http://localhost:8080/api/user/login", {
        username,
        password,
      });

      // Backend sends STRING response like "Login successful"
      if (typeof res.data === "string") {
        alert(res.data);

        // If it's NOT successful, stop
        if (!res.data.toLowerCase().includes("successful")) return;
      }

      // Fetch the full user details after success
      const userRes = await axios.get(
        `http://localhost:8080/api/user/${username}`
      );

      const user = userRes.data; // {id, username, role}

      // Save logged-in user
      localStorage.setItem("currentUser", JSON.stringify(user));
      onLogin(user);

      // Redirect based on role
      if (user.role === "ADMIN") navigate("/admin");
      else navigate("/cart");

    } catch (error) {
      console.error(error);
      alert("Login failed. Please try again.");
    }
  };

  return (
    <Box sx={{ maxWidth: 400, mx: "auto", mt: 5 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        Login
      </Typography>

      <TextField
        label="Username"
        fullWidth
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        sx={{ mb: 2 }}
      />

      <TextField
        label="Password"
        type="password"
        fullWidth
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        sx={{ mb: 2 }}
      />

      <Button variant="contained" fullWidth onClick={handleSubmit}>
        Login
      </Button>

      <Typography sx={{ mt: 2 }}>
        Don't have an account? <Link to="/register">Register here</Link>
      </Typography>
    </Box>
  );
}
