// src/pages/CustomizerPage.js
import React, { useState, useEffect } from "react";
import { Box, Typography, Button, Grid, TextField } from "@mui/material";
import axios from "axios";

const CustomizerPage = ({ productId }) => {
  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    axios.get(`http://localhost:8080/api/products/${productId}`)
      .then(res => setProduct(res.data));

    axios.get(`http://localhost:8080/api/options`)
      .then(res => setOptions(res.data));
  }, [productId]);

  const handleOptionChange = (optionId, value) => {
    setSelectedOptions(prev => ({ ...prev, [optionId]: value }));
  };

  const calculatePrice = () => {
    if (!product) return 0;
    let price = product.price;
    options.forEach(opt => {
      if (selectedOptions[opt.id]) price += opt.price;
    });
    return price * quantity;
  };

  if (!product) return <Typography>Loading...</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>{product.name}</Typography>
      <img src={product.image} alt={product.name} style={{ maxWidth: 300, marginBottom: 20 }} />
      <Grid container spacing={2}>
        {options.map(opt => (
          <Grid item xs={12} sm={6} md={4} key={opt.id}>
            <TextField
              fullWidth
              select
              SelectProps={{ native: true }}
              label={opt.name}
              value={selectedOptions[opt.id] || ""}
              onChange={(e) => handleOptionChange(opt.id, e.target.value)}
            >
              <option value="">None</option>
              {opt.values.map(v => (
                <option key={v} value={v}>{v} (+₹{opt.price})</option>
              ))}
            </TextField>
          </Grid>
        ))}
      </Grid>
      <Box sx={{ mt: 3 }}>
        <TextField
          type="number"
          label="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(Number(e.target.value))}
          sx={{ width: 120, mr: 2 }}
        />
        <Typography variant="h6" display="inline">
          Total: ₹{calculatePrice()}
        </Typography>
      </Box>
      <Box sx={{ mt: 3 }}>
        <Button variant="contained" color="primary">Add to Cart</Button>
      </Box>
    </Box>
  );
};

export default CustomizerPage;
