// src/pages/CartPage.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, Grid } from "@mui/material";
import { getCart, removeFromCart } from "../api/cart"; // make sure removeFromCart exists in your API
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  const fetchCart = () => {
    getCart()
      .then((res) => setCart(res.data))
      .catch(console.error);
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleRemove = (id) => {
    removeFromCart(id)
      .then(() => {
        // Refresh cart after removing
        fetchCart();
      })
      .catch(console.error);
  };

  const total = cart.reduce(
    (sum, it) => sum + (it.totalPrice ?? it.basePrice * (it.quantity || 1)),
    0
  );

  const handlePlaceOrder = () => {
    navigate("/checkout"); // Directly navigate to checkout
  };

  if (!cart.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Grid container spacing={2}>
        {cart.map((item) => (
          <Grid item xs={12} key={item.id}>
            <Paper
              sx={{
                p: 2,
                display: "flex",
                alignItems: "center",
                gap: 2,
              }}
            >
              <img
                src={item.product?.image || item.image || "/images/placeholder.png"}
                alt={item.product?.name || item.name}
                onError={(e) => (e.target.src = "/images/placeholder.png")}
                style={{
                  width: 100,
                  height: 100,
                  objectFit: "cover",
                  borderRadius: 8,
                }}
              />
              <Box sx={{ flex: 1 }}>
                <Typography variant="h6">
                  {item.product?.name || item.name}
                </Typography>
                <Typography>Quantity: {item.quantity}</Typography>
                <Typography>
                  Price: ₹{item.totalPrice ?? item.basePrice * item.quantity}
                </Typography>
              </Box>
              <Button
                variant="outlined"
                color="error"
                onClick={() => handleRemove(item.id)}
              >
                Remove
              </Button>
            </Paper>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h6" sx={{ mt: 2 }}>
        Total: ₹{total}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 3 }}
        onClick={handlePlaceOrder}
      >
        Place Order
      </Button>
    </Box>
  );
}
