import React, { useEffect, useState } from "react";
import { getCart, removeFromCart } from "../api/cart";
import {
  Box,
  Typography,
  Button,
  Grid,
  Paper,
  CircularProgress,
} from "@mui/material";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const fetchCart = async () => {
    try {
      const res = await getCart();
      setCartItems(res.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching cart:", error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleRemove = async (id) => {
    try {
      await removeFromCart(id);
      fetchCart();
    } catch (error) {
      console.error("Error removing item:", error);
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price || 0), 0);
  };

  if (loading) {
    return (
      <Box textAlign="center" mt={5}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box p={3}>
      <Typography variant="h4" mb={3}>
        Your Cart
      </Typography>

      {cartItems.length === 0 ? (
        <Typography>No items in the cart.</Typography>
      ) : (
        <Grid container spacing={2}>
          {cartItems.map((item) => (
            <Grid item xs={12} md={6} key={item.id}>
              <Paper style={{ padding: "16px", display: "flex", gap: "16px" }}>
                
                {/* PRODUCT IMAGE FIX */}
                <img
                  src={`/images/${item.image}`}  
                  alt={item.name}
                  style={{ width: "120px", height: "120px", borderRadius: "10px", objectFit: "cover" }}
                />

                <Box>
                  <Typography variant="h6">{item.name}</Typography>
                  <Typography>Price: ₹{item.price}</Typography>

                  {item.selectedShape && (
                    <Typography>Shape: {item.selectedShape}</Typography>
                  )}

                  {item.selectedToppings?.length > 0 && (
                    <Typography>
                      Toppings: {item.selectedToppings.join(", ")}
                    </Typography>
                  )}

                  <Button
                    color="error"
                    onClick={() => handleRemove(item.id)}
                    sx={{ mt: 2 }}
                  >
                    Remove
                  </Button>
                </Box>
              </Paper>
            </Grid>
          ))}
        </Grid>
      )}

      {cartItems.length > 0 && (
        <Box mt={4}>
          <Typography variant="h5">Total: ₹{calculateTotal()}</Typography>

          <Button
            variant="contained"
            color="primary"
            sx={{ mt: 2 }}
            onClick={() => navigate("/checkout")}
          >
            Proceed to Checkout
          </Button>
        </Box>
      )}
    </Box>
  );
}
