import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  Button,
  Grid,
  Paper,
  CircularProgress
} from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";

export default function CartPage() {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchCart = async () => {
    try {
      const { data } = await getCart();
      setCart(data);
    } catch (error) {
      console.error("Error fetching cart:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleRemove = async (id) => {
    try {
      await removeFromCart(id);
      fetchCart();
    } catch (error) {
      console.error("Error removing item:", error);
    }
  };

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        Your Cart
      </Typography>

      {loading ? (
        <CircularProgress />
      ) : cart.length === 0 ? (
        <Typography variant="h6">Your cart is empty</Typography>
      ) : (
        <Grid container spacing={3}>
          {cart.map((item) => {
            const product = item.product || item; // fallback

            return (
              <Grid item xs={12} md={6} key={item.id}>
                <Paper
                  sx={{
                    p: 2,
                    display: "flex",
                    alignItems: "center",
                    gap: 2
                  }}
                >
                  {/* PRODUCT IMAGE */}
                  <img
                    src={`http://localhost:8080/images/${product.image}`}
                    alt={product.name}
                    style={{
                      width: "100px",
                      height: "100px",
                      objectFit: "cover",
                      borderRadius: "10px"
                    }}
                  />

                  <Box sx={{ flexGrow: 1 }}>
                    <Typography variant="h6">{product.name}</Typography>

                    {/* SHAPE */}
                    {item.selectedShape && (
                      <Typography>
                        Shape: <b>{item.selectedShape}</b>
                      </Typography>
                    )}

                    {/* SIZE */}
                    {item.selectedSize && (
                      <Typography>
                        Size: <b>{item.selectedSize}</b>
                      </Typography>
                    )}

                    {/* TOPPINGS */}
                    {Array.isArray(item.selectedToppings) &&
                      item.selectedToppings.length > 0 && (
                        <Typography>
                          Toppings:{" "}
                          <b>{item.selectedToppings.join(", ")}</b>
                        </Typography>
                      )}

                    <Typography>
                      Quantity: <b>{item.quantity}</b>
                    </Typography>

                    <Typography sx={{ mt: 1 }}>
                      <b>Total: â‚¹{item.totalPrice}</b>
                    </Typography>
                  </Box>

                  <Button
                    variant="contained"
                    color="error"
                    onClick={() => handleRemove(item.id)}
                  >
                    Remove
                  </Button>
                </Paper>
              </Grid>
            );
          })}
        </Grid>
      )}
    </Box>
  );
}
