import React from "react";
import { useParams } from "react-router-dom";
import ProductCard from "../components/ProductCard";
import "./CategoryProducts.css";

function CategoryProducts() {
  const { name } = useParams(); // slug from URL

  // dummy products (later load from backend)
  const allProducts = {
    "bento-cakes": [
      { image: "/images/bento1.jpg", name: "Cute Heart Bento", price: 250 },
      { image: "/images/bento2.jpg", name: "Bear Bento Cake", price: 300 },
    ],
    cupcakes: [
      { image: "/images/cupcake1.jpg", name: "Chocolate Cupcake", price: 80 },
      { image: "/images/cupcake2.jpg", name: "Vanilla Cupcake", price: 70 },
    ],
    pancakes: [
      { image: "/images/pancake1.jpg", name: "Honey Pancakes", price: 150 },
      { image: "/images/pancake2.jpg", name: "Chocolate Pancakes", price: 180 },
    ],
    waffles: [
      { image: "/images/waffle1.jpg", name: "Classic Waffle", price: 160 },
      { image: "/images/waffle2.jpg", name: "Nutella Waffle", price: 190 },
    ],
    "custom-cakes": [
      { image: "/images/customcake1.jpg", name: "Custom Cake 1", price: 400 },
    ],
    cookies: [
      { image: "/images/cookie1.jpg", name: "Chocolate Chip Cookie", price: 50 },
      { image: "/images/cookie2.jpg", name: "Butter Cookie", price: 40 },
    ],
  };

  const products = allProducts[name] || [];

  // convert slug to readable title
  const pageTitle = name.replace(/-/g, " ").toUpperCase();

  return (
    <div className="category-products">
      <h2>{pageTitle}</h2>

      <div className="products-grid">
        {products.length > 0 ? (
          products.map((p, i) => (
            <ProductCard key={i} image={p.image} name={p.name} price={p.price} />
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
    </div>
  );
}

export default CategoryProducts;
