// src/pages/CartPage.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, Grid } from "@mui/material";
import { getCart } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getCart()
      .then((res) => setCart(res.data))
      .catch(console.error);
  }, []);

  const total = cart.reduce(
    (sum, it) => sum + (it.totalPrice ?? it.basePrice * (it.quantity || 1)),
    0
  );

  const handlePlaceOrder = () => {
    navigate("/checkout");
  };

  if (!cart.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Grid container spacing={2}>
        {cart.map((item) => (
          <Grid item xs={12} key={item.id}>
<Paper sx={{ p: 2, display: "flex", gap: 2 }}>
  {/* Product Image */}
  <img
    src={`/images/${item.product?.image || item.image}`}
    alt={item.product?.name}
    style={{
      width: 100,
      height: 100,
      objectFit: "cover",
      borderRadius: 8
    }}
  />

  {/* Details */}
  <Box>
    <Typography variant="h6">
      {item.product?.name || item.name}
    </Typography>

    {/* Show Customizations */}
    {item.selectedSize && (
      <Typography>Size: {item.selectedSize.name}</Typography>
    )}

    {item.selectedShape && (
      <Typography>Shape: {item.selectedShape.name}</Typography>
    )}

    {item.selectedFlavor && (
      <Typography>Flavor: {item.selectedFlavor.name}</Typography>
    )}

    {/* Toppings (Array) */}
    {item.selectedToppings?.length > 0 && (
      <Typography>
        Toppings: {item.selectedToppings.map(t => t.name).join(", ")}
      </Typography>
    )}

    <Typography>Quantity: {item.quantity}</Typography>

    <Typography>
      Price: ₹{item.totalPrice ?? item.basePrice * item.quantity}
    </Typography>
  </Box>
</Paper>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h6" sx={{ mt: 2 }}>
        Total: ₹{total}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 3 }}
        onClick={handlePlaceOrder}
      >
        Place Order
      </Button>
    </Box>
  );
}
