// src/api/cart.js
import axios from "axios";

const API = "http://localhost:8080/api/cart";

export const getCart = () => axios.get(API);

export const addToCart = (item, username) =>
  axios.post(`${API}/add`, {
    ...item,
    username: username
  });

export const removeFromCart = (id) =>
  axios.delete(`${API}/${id}`);

export const updateCartItem = (id, updatedData) =>
  axios.put(`${API}/${id}`, updatedData);
