import React, { useEffect, useState, useCallback } from "react";
import { Box, Typography, Paper, Button, TextField, Grid, Stack } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

// Option mappings
const OPTION_LABELS = {
size: { 4: "Small", 5: "Medium", 6: "Large" },
flavor: { 1: "Chocolate", 2: "Vanilla", 3: "Strawberry" },
shape: { 7: "Round", 8: "Heart", 9: "Square" },
toppings: { 10: "Choco Chips", 11: "Rainbow Sprinkler", 12: "Nuts", 13: "Fruits", 14: "Cherries" },
};

// Safe JSON parse
const safeParse = (val) => {
if (!val) return null;
try { return JSON.parse(val); } catch { return null; }
};

export default function CartPage({ currentUser }) {
const [items, setItems] = useState([]);
const navigate = useNavigate();

// loadCart wrapped in useCallback to avoid ESLint warning
const loadCart = useCallback(async () => {
if (!currentUser?.username) return;
try {
const res = await getCart(currentUser.username);
setItems(res.data || []);
} catch (err) {
console.error("Failed to load cart:", err);
}
}, [currentUser?.username]);

useEffect(() => {
if (!currentUser?.username) {
alert("Please login or register first to view your cart!");
navigate("/login");
return;
}
loadCart();
}, [currentUser, loadCart, navigate]);

const removeItem = async (id) => {
Â  try {
Â  Â  await removeFromCart(id, currentUser.username);
Â  Â  loadCart();
Â  } catch (err) {
Â  Â  console.error("Failed to remove item:", err);
Â  }
};

const updateQty = async (id, qty) => {
if (qty < 1) return;
try {
await updateCartItem(id, { quantity: qty });
loadCart();
} catch (err) {
console.error("Failed to update quantity:", err);
}
};

const totalPrice = items.reduce((sum, it) => sum + (it.totalPrice ?? it.basePrice * it.quantity), 0);

if (!items.length) return (
    <Box sx={{ p: 4, textAlign: 'center' }}>
        <Typography variant="h5" color="text.secondary">
            Your cart is empty. Start adding some delicious items! ðŸ˜‹
        </Typography>
        <Button variant="contained" sx={{ mt: 3, bgcolor: '#A00045' }} onClick={() => navigate("/")}>
            Start Shopping
        </Button>
    </Box>
);

return ( 
    <Box sx={{ p: { xs: 2, md: 4 }, maxWidth: 900, mx: 'auto' }}>
        <Typography 
            variant="h4" 
            sx={{ 
                mb: 4, 
                fontWeight: 'bold', 
                color: '#A00045', 
                borderBottom: '3px solid #f8bbd0', 
                pb: 1 
            }}
        >
            My Cart
        </Typography> 
        
        <Box display="grid" gap={3}>
            {items.map((it) => {
                const selected = safeParse(it.selectedSingle) || {};
                const toppings = safeParse(it.selectedToppings) || [];
                return (
                    <Paper 
                        key={it.id} 
                        elevation={4} 
                        sx={{ 
                            display: "flex", 
                            p: 3, 
                            borderRadius: 3, 
                            alignItems: "stretch", 
                            bgcolor: '#ffffff'
                        }}
                    >
                        
                        {/* 1. Image */}
                        <Box sx={{ flexShrink: 0, mr: 3 }}>
                            <img
                                src={`/images/${it.image}`}
                                alt={it.name}
                                style={{ 
                                    width: 120, 
                                    height: 120,
                                    borderRadius: 8, 
                                    objectFit: "cover",
                                    border: '1px solid #eee'
                                }}
                            />
                        </Box>

                        {/* 2. Details */}
                        <Box sx={{ flexGrow: 1, minWidth: '50%' }}> 
                            <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#333' }}>
                                {it.name}
                            </Typography> 
                            
                            <Stack spacing={0.5} sx={{ mt: 1 }}>
                                <Typography variant="body2" color="text.secondary">
                                    <Box component="span" sx={{ fontWeight: 'bold' }}>Size:</Box> {OPTION_LABELS.size[selected.size] ?? "None"}
                                </Typography> 
                                <Typography variant="body2" color="text.secondary">
                                    <Box component="span" sx={{ fontWeight: 'bold' }}>Flavor:</Box> {OPTION_LABELS.flavor[selected.flavor] ?? "None"}
                                </Typography> 
                                <Typography variant="body2" color="text.secondary">
                                    <Box component="span" sx={{ fontWeight: 'bold' }}>Shape:</Box> {OPTION_LABELS.shape[selected.shape] ?? "None"}
                                </Typography> 
                                <Typography variant="body2" color="text.secondary"> 
                                    <Box component="span" sx={{ fontWeight: 'bold' }}>Toppings:</Box> {toppings.length ? toppings.map(id => OPTION_LABELS.toppings[id]).join(", ") : "None"} 
                                </Typography>
                            </Stack>
                            {/* Display Custom Message if present */}
                            {it.customMessage && (
                                <Box sx={{ mt: 1, p: 1, bgcolor: '#fdf3f8', borderLeft: '3px solid #A00045', fontSize: '0.85rem' }}>
                                    **Message:** {it.customMessage}
                                </Box>
                            )}
                        </Box>
                        
                        {/* 3. Quantity and Actions */}
                        <Box 
                            sx={{ 
                                textAlign: "right", 
                                display: 'flex', 
                                flexDirection: 'column', 
                                justifyContent: 'space-between',
                                alignItems: 'flex-end',
                                ml: 2,
                                borderLeft: '1px dashed #eee',
                                pl: 2
                            }}
                        >
                            <TextField
                                type="number"
                                label="Qty"
                                value={it.quantity}
                                onChange={(e) => updateQty(it.id, Math.max(1, Number(e.target.value)))}
                                sx={{ width: 70, mb: 1 }}
                                InputProps={{ inputProps: { min: 1 } }}
                                size="small"
                            /> 
                            <Typography 
                                variant="h6" 
                                sx={{ fontWeight: 'bold', color: '#A00045' }}
                            >
                                â‚¹{it.totalPrice ?? it.basePrice * it.quantity}
                            </Typography>
                            <Button 
                                color="error" 
                                size="small"
                                sx={{ mt: 1 }}
                                onClick={() => removeItem(it.id)}
                            >
                                Remove
                            </Button> 
                        </Box> 
                    </Paper>
                );
            })} 
        </Box>
        
        {/* Cart Summary and Checkout */}
        <Paper elevation={4} sx={{ mt: 4, p: 3, bgcolor: '#fefefe', borderRadius: 3, textAlign: 'right' }}>
            <Typography variant="h5" sx={{ fontWeight: 'bold', color: '#333' }}>
                Cart Subtotal: <Box component="span" sx={{ color: '#A00045', ml: 1 }}>â‚¹{totalPrice}</Box>
            </Typography>
            <Button 
                variant="contained" 
                color="primary" 
                sx={{ 
                    mt: 2, 
                    py: 1.2, 
                    fontSize: '1.1rem', 
                    bgcolor: '#A00045',
                    '&:hover': { bgcolor: '#800037' }
                }} 
                onClick={() => navigate("/checkout")}
            >
                Proceed to Checkout
            </Button> 
        </Paper>
    </Box>
);
}
