// src/pages/OrdersPage.js (UPDATED CODE)
import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, CircularProgress, CardMedia } from "@mui/material";
import { getAllOrders } from "../api/orders";

// Option ID → Label mappings
const OPTION_LABELS = {
    size: { 4: "Small", 5: "Medium", 6: "Large" },
    flavor: { 1: "Chocolate", 2: "Vanilla", 3: "Strawberry" },
    shape: { 7: "Round", 8: "Heart", 9: "Square" },
    toppings: { 10: "Choco Chips", 11: "Rainbow Sprinkler", 12: "Nuts", 13: "Fruits", 14: "Cherries" },
};

// Safe parser for selectedOptions JSON
const safeParseOptions = (val) => {
    if (!val) return { single: {}, toppings: [] };
    try {
        const parsed = JSON.parse(val);
        return {
            single: parsed.single || {},
            toppings: Array.isArray(parsed.toppings) ? parsed.toppings : []
        };
    } catch {
        return { single: {}, toppings: [] };
    }
};

export default function OrdersPage() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    // --- Order Fetching Logic (Same as before) ---
    useEffect(() => {
        const currentUserJSON = localStorage.getItem("currentUser");
        if (!currentUserJSON) {
            setOrders([]);
            setLoading(false);
            return;
        }

        let username;
        try {
            const currentUser = JSON.parse(currentUserJSON);
            username = currentUser.username;
        } catch {
            setOrders([]);
            setLoading(false);
            return;
        }

        if (!username) {
            setOrders([]);
            setLoading(false);
            return;
        }

        getAllOrders()
            .then((res) => {
                const userOrders = res.data.filter(
                    (order) => order.userName.toLowerCase() === username.toLowerCase()
                );
                setOrders(userOrders);
            })
            .catch(console.error)
            .finally(() => setLoading(false));
    }, []);
    // ---------------------------------------------

    // Render Size, Flavor, Shape, and Toppings
    const renderCustomOptions = (item) => {
        const { single, toppings } = safeParseOptions(item.selectedOptions);

        const toppingNames = toppings
            .map((id) => OPTION_LABELS.toppings[id])
            .filter(Boolean)
            .join(", ");

        const hasOptions = Object.keys(single).length > 0 || toppings.length > 0;

        return (
            <Box sx={{ mt: 1 }}>
                <Typography variant="subtitle2" color="textSecondary">Custom Options:</Typography>
                {hasOptions ? (
                    <>
                        {Object.entries(single).map(([key, id]) => (
                            <Typography key={key} sx={{ pl: 2, fontSize: '0.9rem' }}>
                                {key.charAt(0).toUpperCase() + key.slice(1)}: {OPTION_LABELS[key]?.[id] ?? 'N/A'}
                            </Typography>
                        ))}
                        {toppingNames && (
                            <Typography sx={{ pl: 2, fontSize: '0.9rem' }}>
                                Toppings: {toppingNames}
                            </Typography>
                        )}
                    </>
                ) : (
                    <Typography sx={{ pl: 2, fontSize: '0.9rem' }}>None</Typography>
                )}
            </Box>
        );
    };

    if (loading)
        return <CircularProgress sx={{ mt: 5, display: "block", mx: "auto" }} />;

    if (!orders.length)
        return <Typography sx={{ p: 3 }}>You have not placed any orders yet.</Typography>;

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h4" sx={{ mb: 3 }}>
                My Orders
            </Typography>

            <Grid container spacing={4}> {/* Increased spacing for better look */}
                {orders.map((order) => (
                    <Grid item xs={12} sm={6} md={4} key={order.id}>
                        <Paper sx={{ p: 3, height: '100%', borderLeft: '4px solid #FF4081', boxShadow: 3 }}>
                            <Typography variant="h6" color="primary">Order ID: **{order.id}**</Typography>
                            <Typography>Total: **₹{order.totalPrice}**</Typography>
                            <Typography>Delivery Date: **{new Date(order.deliveryDate).toLocaleDateString()}**</Typography>
                            <Typography sx={{ mb: 2 }}>Delivery Slot: **{order.deliverySlot}**</Typography>

                            <hr />

                            <Box sx={{ mt: 2 }}>
                                <Typography variant="subtitle1" fontWeight="bold">Items Ordered:</Typography>
                                {order.items && order.items.length ? (
                                    order.items.map((item) => (
                                        <Paper key={item.id} sx={{ p: 1.5, mt: 1.5, display: 'flex', gap: 2, alignItems: 'center', backgroundColor: '#FCE4EC' }}>
                                            
                                            {/* --- IMAGE DISPLAY --- */}
                                            {item.imageUrl && (
                                                <CardMedia
                                                    component="img"
                                                    sx={{ 
                                                        width: 60, 
                                                        height: 60, 
                                                        borderRadius: 1, 
                                                        flexShrink: 0, 
                                                        objectFit: 'cover'
                                                    }}
                                                    image={item.imageUrl} // Assumes item has imageUrl
                                                    alt={item.product?.name ?? item.name}
                                                />
                                            )}
                                            {/* --------------------- */}

                                            <Box>
                                                <Typography fontWeight="bold">{item.product?.name ?? item.name}</Typography>
                                                <Typography variant="body2">Qty: {item.quantity} | Price: ₹{item.finalPrice}</Typography>
                                                {renderCustomOptions(item)}
                                            </Box>
                                        </Paper>
                                    ))
                                ) : (
                                    <Typography>No items found</Typography>
                                )}
                            </Box>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
        </Box>
    );
}
