import React, { useState, useEffect } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Box,
  Button,
  Badge,
} from "@mui/material";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import CakeIcon from "@mui/icons-material/Cake";
import { Link, useNavigate } from "react-router-dom";
import { getCart } from "../api/cart";

export default function Navbar() {
  const [count, setCount] = useState(0);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user"))); // read once
  const navigate = useNavigate();

  // Update cart count
  const updateCartCount = () => {
    getCart()
      .then((res) => {
        const items = res.data || [];
        const c = items.reduce((s, it) => s + (it.quantity || 1), 0);
        setCount(c);
      })
      .catch(() => setCount(0));
  };

  useEffect(() => {
    updateCartCount();

    // Listen to storage changes (for login/logout in other tabs)
    const handleStorageChange = () => {
      setUser(JSON.parse(localStorage.getItem("user")));
      updateCartCount();
    };
    window.addEventListener("storage", handleStorageChange);

    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    alert("Logged out successfully!");
    navigate("/login");
  };

  return (
    <AppBar
      position="sticky"
      sx={{ background: "#ffebf0", borderBottom: "1px solid #ffd6df" }}
    >
      <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
        {/* Logo */}
        <Box
          display="flex"
          alignItems="center"
          component={Link}
          to="/"
          sx={{ textDecoration: "none" }}
        >
          <CakeIcon sx={{ color: "#ff4f7b", mr: 1 }} />
          <Typography variant="h6" sx={{ color: "#ff4f7b", fontWeight: "700" }}>
            SweetCraft
          </Typography>
        </Box>

        {/* Middle Menu */}
        <Box>
          <Button component={Link} to="/" sx={{ color: "#a32456" }}>
            Home
          </Button>
          <Button component={Link} to="/categories" sx={{ color: "#a32456" }}>
            Categories
          </Button>

          {/* Show My Orders only if logged in */}
          {user && (
            <Button component={Link} to="/orders" sx={{ color: "#a32456" }}>
              My Orders
            </Button>
          )}
        </Box>

        {/* Right Side */}
        <Box display="flex" alignItems="center">
          <IconButton onClick={() => navigate("/cart")} sx={{ color: "#ff4f7b" }}>
            <Badge badgeContent={count} color="primary">
              <ShoppingCartIcon />
            </Badge>
          </IconButton>

          {!user ? (
            <>
              <Button component={Link} to="/login" sx={{ color: "#a32456", ml: 1 }}>
                Login
              </Button>
              <Button component={Link} to="/register" sx={{ color: "#a32456", ml: 1 }}>
                Register
              </Button>
            </>
          ) : (
            <Button onClick={handleLogout} sx={{ color: "#d72638", ml: 1 }}>
              Logout
            </Button>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
}
