import React from "react";
import {
  FormControl,
  FormLabel,
  FormGroup,
  FormControlLabel,
  Checkbox
} from "@mui/material";

export default function ToppingsSelector({ title, options = [], selected = [], onChange }) {

  const normalizeLabel = (opt) => {
    const displayName = opt.value || opt.name;
    const price = opt.extraPrice ?? opt.extra_price ?? 0;
    return price ? `${displayName} (+₹${price})` : displayName;
  };

  const handleToggle = (id) => {
    const updated = selected.includes(id)
      ? selected.filter(x => x !== id)
      : [...selected, id];
    onChange(updated);
  };

  return (
    <FormControl component="fieldset" sx={{ mt: 3 }}>
      <FormLabel component="legend" sx={{ mb: 1, fontWeight: 600 }}>
        {title}
      </FormLabel>

      <FormGroup>
        {options.map((opt) => (
          <FormControlLabel
            key={opt.id}
            control={
              <Checkbox
                checked={selected.includes(opt.id.toString())} // ensure ID as string
                onChange={() => handleToggle(opt.id.toString())}
              />
            }
            label={normalizeLabel(opt)}
          />
        ))}
      </FormGroup>
    </FormControl>
  );
}
