// src/pages/OrdersPage.js

import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, CircularProgress } from "@mui/material";
import { getAllOrders } from "../api/orders";

// 1. Define the Option Mappings (Copied from CartPage.js)
const OPTION_LABELS = {
    size: { 4: "Small", 5: "Medium", 6: "Large" },
    flavor: { 1: "Chocolate", 2: "Vanilla", 3: "Strawberry" },
    shape: { 7: "Round", 8: "Heart", 9: "Square" },
    toppings: { 10: "Choco Chips", 11: "Rainbow Sprinkler", 12: "Nuts", 13: "Fruits", 14: "Cherries" },
};

// 2. Safe JSON parse function
const safeParse = (val) => {
    if (!val) return null;
    try {
        const parsed = JSON.parse(val);

        if (parsed.selectedSingle) {
            return {
                single: parsed.selectedSingle,
                toppings: parsed.selectedToppings,
            };
        }
        
        return {
            single: Object.fromEntries(Object.entries(parsed).filter(([key]) => key !== 'toppings')),
            toppings: parsed.toppings || [],
        };
    } catch {
        return null;
    }
};

export default function OrdersPage() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // âŒ ERROR FIX: Declaring 'username' here so it's accessible later.
        let username; 
        
        // Correctly retrieve and parse the currentUser object
        const currentUserJSON = localStorage.getItem("currentUser");
        if (!currentUserJSON) {
            setOrders([]);
            setLoading(false);
            return;
        }

        try {
            const currentUser = JSON.parse(currentUserJSON);
            username = currentUser.username; // 'username' is now defined in the outer scope
        } catch (e) {
            setOrders([]);
            setLoading(false);
            return;
        }

        // Check if username was successfully extracted
        if (!username) {
            setOrders([]);
            setLoading(false);
            return;
        }

        getAllOrders()
            .then((res) => {
                // Filter orders by logged-in user (case-insensitive)
                const userOrders = res.data.filter(
                    (order) => order.userName.toLowerCase() === username.toLowerCase()
                );
                setOrders(userOrders);
            })
            .catch(console.error)
            .finally(() => setLoading(false));
    }, []); // Empty dependency array is fine here

    if (loading)
        return <CircularProgress sx={{ mt: 5, display: "block", mx: "auto" }} />;

    if (!orders.length)
        return <Typography sx={{ p: 3 }}>You have not placed any orders yet.</Typography>;

// src/pages/OrdersPage.js
// ... (Lines 1-79: OPTION_LABELS, safeParse, and useEffect logic remain the same)

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h4" sx={{ mb: 3 }}>
                My Orders
            </Typography>

            <Grid container spacing={2}>
                {orders.map((order) => (
                    <Grid item xs={12} key={order.id}>
                        <Paper sx={{ p: 2 }}>
                            <Typography variant="h6">Order ID: {order.id}</Typography>
                            {/* ... (Other order details) */}
                            <Typography>Delivery Slot: {order.deliverySlot}</Typography>

                            <Box sx={{ mt: 2 }}>
                                <Typography variant="subtitle1">Items:</Typography>

                                {order.items && order.items.length ? (
                                    order.items.map((item) => {
                                        // Option processing logic
                                        const optionsData = safeParse(item.selectedOptions) || {};
                                        const { single = {}, toppings = [] } = optionsData; 
                                        
                                        // Extract specific single options, defaulting to N/A
                                        const size = OPTION_LABELS.size[single.size] || 'N/A';
                                        const flavor = OPTION_LABELS.flavor[single.flavor] || 'N/A';
                                        const shape = OPTION_LABELS.shape[single.shape] || 'N/A';
                                        
                                        // Format toppings array to string
                                        const toppingNames = toppings
                                            .map(id => OPTION_LABELS.toppings[id])
                                            .filter(Boolean)
                                            .join(", ");
                                        

                                        return (
                                            <Paper key={item.id} sx={{ p: 1, mt: 1 }}>
                                                <Typography>Name: {item.product?.name ?? item.name}</Typography>
                                                <Typography>Quantity: {item.quantity}</Typography>
                                                <Typography>Price: â‚¹{item.finalPrice}</Typography>

                                                <Box sx={{ mt: 1 }}>
                                                    <Typography variant="subtitle2">Options:</Typography>
                                                    
                                                    {/* ðŸš€ Display Size, Flavor, and Shape individually */}
                                                    {size !== 'N/A' && 
                                                        <Typography sx={{ pl: 2 }}>Size: **{size}**</Typography>
                                                    }
                                                    {flavor !== 'N/A' && 
                                                        <Typography sx={{ pl: 2 }}>Flavor: **{flavor}**</Typography>
                                                    }
                                                    {shape !== 'N/A' && 
                                                        <Typography sx={{ pl: 2 }}>Shape: **{shape}**</Typography>
                                                    }

                                                    {/* ðŸš€ Display Toppings */}
                                                    {toppingNames.length > 0 && 
                                                        <Typography sx={{ pl: 2 }}>Toppings: **{toppingNames}**</Typography>
                                                    }
                                                    
                                                    {/* Fallback for "Options: None" */}
                                                    {(size === 'N/A' && flavor === 'N/A' && shape === 'N/A' && toppingNames.length === 0) &&
                                                        <Typography sx={{ pl: 2 }}>None</Typography>
                                                    }
                                                </Box>
                                            </Paper>
                                        );
                                    })
                                ) : (
                                    <Typography>No items found</Typography>
                                )}
                            </Box>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
        </Box>
    );
}

