// src/components/Navbar.js
import React, { useEffect, useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Box,
  Button,
  Badge,
} from "@mui/material";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import CakeIcon from "@mui/icons-material/Cake";
import { Link, useNavigate } from "react-router-dom";
import { getCart } from "../api/cart";

export default function Navbar({ currentUser, onLogout }) {
  const [count, setCount] = useState(0);
  const navigate = useNavigate();

  // Fetch cart count when user changes
  useEffect(() => {
    if (!currentUser || currentUser.role === "ADMIN") {
      setCount(0);
      return;
    }

    getCart(currentUser.username)
      .then((res) => {
        const items = res.data || [];
        const total = items.reduce((s, it) => s + (it.quantity || 1), 0);
        setCount(total);
      })
      .catch(() => setCount(0));
  }, [currentUser]);

  return (
    <AppBar
      position="sticky"
      sx={{ background: "#ffebf0", borderBottom: "1px solid #ffd6df" }}
    >
      <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
        
        {/* Logo */}
        <Box
          display="flex"
          alignItems="center"
          component={Link}
          to="/"
          sx={{ textDecoration: "none" }}
        >
          <CakeIcon sx={{ color: "#ff4f7b", mr: 1 }} />
          <Typography variant="h6" sx={{ color: "#ff4f7b", fontWeight: "700" }}>
            SweetCraft
          </Typography>
        </Box>

        {/* Middle Menu */}
        <Box>
          <Button component={Link} to="/" sx={{ color: "#a32456" }}>
            Home
          </Button>

          <Button component={Link} to="/categories" sx={{ color: "#a32456" }}>
            Categories
          </Button>

          {/* Hide My Orders for Admin */}
          {currentUser?.role !== "ADMIN" && (
            <Button component={Link} to="/orders" sx={{ color: "#a32456" }}>
              My Orders
            </Button>
          )}

          {/* Show Admin Panel only for Admin */}
          {currentUser?.role === "ADMIN" && (
            <Button
              component={Link}
              to="/admin"
              sx={{ color: "#a32456", fontWeight: "bold" }}
            >
              Admin Panel
            </Button>
          )}
        </Box>

        {/* Right Side */}
        <Box display="flex" alignItems="center">
          {!currentUser ? (
            <>
              <Button onClick={() => navigate("/login")}>Login</Button>
              <Button onClick={() => navigate("/register")}>Register</Button>
            </>
          ) : (
            <>
              <Typography sx={{ mr: 2, fontWeight: "bold" }}>
                {currentUser.username}
              </Typography>
              <Button onClick={onLogout}>Logout</Button>
            </>
          )}

          {/* Cart Icon â†’ hidden for ADMIN */}
          {currentUser?.role !== "ADMIN" && (
            <IconButton onClick={() => navigate("/cart")} sx={{ color: "#ff4f7b" }}>
              <Badge badgeContent={count} color="primary">
                <ShoppingCartIcon />
              </Badge>
            </IconButton>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
}
