// src/pages/Cart.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Button, TextField, Paper } from "@mui/material";
import { getCart, removeFromCart, calculateCartPrice } from "../api/cart";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);

  const fetchCart = () => {
    getCart().then(res => setCartItems(res.data)).catch(err => console.error(err));
  };

  useEffect(() => {
    fetchCart();
  }, []);

  useEffect(() => {
    const payload = cartItems.map(item => ({
      basePrice: item.basePrice,
      quantity: item.quantity,
      selectedSingle: item.selectedSingle,
      selectedToppings: item.selectedToppings
    }));

    if (payload.length) {
      calculateCartPrice(payload)
        .then(res => setTotal(res.data.total))
        .catch(err => console.error(err));
    } else {
      setTotal(0);
    }
  }, [cartItems]);

  const handleRemove = (id) => {
    removeFromCart(id).then(fetchCart).catch(err => console.error(err));
  };

  const handleQuantityChange = (id, value) => {
    setCartItems(prev => prev.map(item => item.id === id ? { ...item, quantity: Math.max(1, value) } : item));
  };

  if (!cartItems.length) return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>Your Cart</Typography>
      <Grid container spacing={2}>
        {cartItems.map(item => (
          <Grid item xs={12} key={item.id}>
            <Paper sx={{ display: "flex", alignItems: "center", p: 2 }}>
              <img src={item.image} alt={item.name} style={{ width: "100px", borderRadius: "10px", marginRight: "20px" }} />
              <Box sx={{ flexGrow: 1 }}>
                <Typography variant="h6">{item.name}</Typography>
                <Typography variant="body2">Base Price: ₹{item.basePrice}</Typography>
                <Typography variant="body2">
                  Single Options: {JSON.stringify(item.selectedSingle)}
                </Typography>
                <Typography variant="body2">
                  Toppings: {item.selectedToppings.join(", ")}
                </Typography>
              </Box>
              <Box sx={{ display: "flex", flexDirection: "column", alignItems: "flex-end" }}>
                <TextField
                  type="number"
                  label="Qty"
                  value={item.quantity}
                  onChange={e => handleQuantityChange(item.id, parseInt(e.target.value))}
                  InputProps={{ inputProps: { min: 1 } }}
                  sx={{ mb: 1, width: "80px" }}
                />
                <Button variant="contained" color="error" onClick={() => handleRemove(item.id)}>Remove</Button>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Typography variant="h5" sx={{ mt: 3 }}>Total Price: ₹{total}</Typography>
      <Button variant="contained" color="secondary" sx={{ mt: 2 }}>Proceed to Checkout</Button>
    </Box>
  );
};

export default Cart;
