import React, { useEffect, useState } from "react";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function Cart() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  const fetchCart = () => getCart().then(res => setCart(res.data));

  useEffect(() => { fetchCart(); }, []);

  const handleRemove = (id) => {
    removeFromCart(id).then(fetchCart);
  };

  const handleQuantityChange = (id, quantity) => {
    if (quantity < 1) return;
    updateCartItem(id, { quantity }).then(fetchCart);
  };

  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  if (cart.length === 0) return <p style={{ padding: "40px" }}>Cart is empty.</p>;

  return (
    <div style={{ padding: "40px" }}>
      <h2>Your Cart</h2>
      {cart.map(item => (
        <div key={item.id} style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
          <div>
            <h3>{item.productName}</h3>
            <p>Options: {Object.entries(item.options || {}).map(([k,v]) => `${k}: ${v}`).join(", ")}</p>
          </div>
          <div>
            <input type="number" value={item.quantity} min={1} onChange={e => handleQuantityChange(item.id, parseInt(e.target.value))} style={{ width: "60px", marginRight: "10px" }} />
            <span>₹{item.price * item.quantity}</span>
            <button onClick={() => handleRemove(item.id)} style={{ marginLeft: "10px" }}>Remove</button>
          </div>
        </div>
      ))}
      <h3>Total: ₹{total}</h3>
      <button onClick={() => navigate("/checkout")}>Proceed to Checkout</button>
    </div>
  );
}
