import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, Button, TextField } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

// ID → Label mappings
const OPTION_LABELS = {
  size: { 5: "Small", 6: "Medium", 7: "Large" },
  flavor: { 8: "Chocolate", 9: "Vanilla", 10: "Strawberry" },
  shape: { 2: "Round", 3: "Heart", 8: "Square" },
  toppings: {
    12: "Choco Chips",
    14: "Rainbow Sprinkles",
    18: "Nuts",
    20: "Fruits",
    21: "Cherries"
  }
};

// normalize backend values into an array
const toArray = (value) => {
  if (!value) return [];
  if (Array.isArray(value)) return value;
  if (typeof value === "string") {
    return value.replace(/[\[\]\s]/g, "").split(",").filter(Boolean);
  }
  return [];
};

export default function CartPage() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  const fetch = () => getCart().then((r) => setItems(r.data));

  useEffect(fetch, []);

  const handleRemove = (id) => removeFromCart(id).then(fetch);

  const handleQty = (id, qty) => {
    if (qty < 1) return;
    updateCartItem(id, { quantity: qty }).then(fetch);
  };

  const total = items.reduce(
    (s, it) => s + (it.totalPrice ?? it.basePrice * it.quantity),
    0
  );

  if (!items.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 3 }}>My Cart</Typography>

      <Grid container spacing={2}>
        {items.map((it) => {
          // normalize incoming values
          const selected = it.selectedSingle || {};
          const toppingsArray = toArray(it.selectedToppings);

          return (
            <Grid item xs={12} key={it.id}>
              <Paper sx={{ display: "flex", p: 2 }}>
                
                {/* Image */}
                <img
                  src={`/images/${it.image}`}
                  alt={it.name}
                  style={{ width: 120, borderRadius: 8, marginRight: 16 }}
                />

                <Box sx={{ flex: 1 }}>
                  <Typography variant="h6">{it.name}</Typography>

                  {/* ⭐ Human Readable Selections */}
                  <Typography variant="body2">
                    <b>Size:</b> {OPTION_LABELS.size[selected.size] || "None"}
                  </Typography>

                  <Typography variant="body2">
                    <b>Flavor:</b> {OPTION_LABELS.flavor[selected.flavor] || "None"}
                  </Typography>

                  <Typography variant="body2">
                    <b>Shape:</b> {OPTION_LABELS.shape[selected.shape] || "None"}
                  </Typography>

                  <Typography variant="body2">
                    <b>Toppings:</b>{" "}
                    {toppingsArray.length
                      ? toppingsArray
                          .map((id) => OPTION_LABELS.toppings[id] || id)
                          .join(", ")
                      : "None"}
                  </Typography>
                </Box>

                <Box sx={{ textAlign: "right" }}>
                  <TextField
                    type="number"
                    value={it.quantity}
                    onChange={(e) =>
                      handleQty(it.id, Math.max(1, parseInt(e.target.value || "1")))
                    }
                    sx={{ width: 80, mb: 1 }}
                    InputProps={{ inputProps: { min: 1 } }}
                  />

                  <Typography>₹{it.totalPrice ?? it.basePrice * it.quantity}</Typography>

                  <Button color="error" onClick={() => handleRemove(it.id)}>
                    Remove
                  </Button>
                </Box>
              </Paper>
            </Grid>
          );
        })}
      </Grid>

      <Typography variant="h5" sx={{ mt: 3 }}>Total: ₹{total}</Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 2 }}
        onClick={() => navigate("/checkout")}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );
}
