import axios from "axios";
const BASE_URL = "http://localhost:8080/api/options";
export const getAllOptions = () => axios.get(`${BASE_URL}/all`);
export const getOptionsByType = (type) => axios.get(`${BASE_URL}/type/${type}`);
export const addOption = (option) => axios.post(`${BASE_URL}/add`, option);
