import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, Button, TextField } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

// üî• Mapping IDs ‚Üí Labels (required because backend stores only IDs)
const SIZE_MAP = {
  5: "Small",
  6: "Medium",
  7: "Large"
};

const FLAVOR_MAP = {
  8: "Chocolate",
  9: "Vanilla",
  10: "Strawberry"
};

const SHAPE_MAP = {
  11: "Round",
  12: "Heart",
  13: "Square"
};

const TOPPINGS_MAP = {
  12: "Choco Chips",
  14: "Nuts",
  15: "Rainbow Sprinkles",
  16: "Fruits",
  17: "Cherries"
};

export default function CartPage() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  const fetch = () => {
    getCart().then(res => setItems(res.data)).catch(console.error);
  };

  useEffect(() => { fetch(); }, []);

  const handleRemove = (id) => {
    removeFromCart(id).then(fetch).catch(console.error);
  };

  const handleQty = (id, qty) => {
    if (qty < 1) return;
    updateCartItem(id, { quantity: qty }).then(fetch).catch(console.error);
  };

  const total = items.reduce(
    (s, it) => s + (it.totalPrice ?? it.basePrice * (it.quantity || 1)),
    0
  );

  const displayOrNone = (value) => value ? value : "None";

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 2 }}>My Cart</Typography>

      <Grid container spacing={2}>
        {items.map(it => (
          <Grid item xs={12} key={it.id}>
            <Paper sx={{ display: "flex", alignItems: "center", p: 2 }}>

              <img
                src={`/images/${it.image}`}
                alt={it.name}
                style={{ width: 120, borderRadius: 8, marginRight: 16 }}
              />

              <Box sx={{ flex: 1 }}>
                <Typography variant="h6">{it.name}</Typography>

                {/* ‚≠ê Human readable selections */}
                <Typography>
                  <b>Size:</b> {SIZE_MAP[it.size] || "None"}
                </Typography>

                <Typography>
                  <b>Flavor:</b> {FLAVOR_MAP[it.flavor] || "None"}
                </Typography>

                <Typography>
                  <b>Shape:</b> {SHAPE_MAP[it.shape] || "None"}
                </Typography>

                <Typography>
                  <b>Toppings:</b>{" "}
                  {it.selectedToppings?.length
                    ? it.selectedToppings.map(id => TOPPINGS_MAP[id]).join(", ")
                    : "None"}
                </Typography>
              </Box>

              <Box sx={{ display: "flex", flexDirection: "column", alignItems: "flex-end" }}>
                <TextField
                  type="number"
                  value={it.quantity}
                  onChange={(e) =>
                    handleQty(it.id, Math.max(1, parseInt(e.target.value || "1")))
                  }
                  sx={{ width: 90, mb: 1 }}
                  InputProps={{ inputProps: { min: 1 } }}
                />

                <Typography variant="subtitle1">
                  ‚Çπ{it.totalPrice ?? it.basePrice * it.quantity}
                </Typography>

                <Button color="error" onClick={() => handleRemove(it.id)} sx={{ mt: 1 }}>
                  Remove
                </Button>
              </Box>

            </Paper>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h5" sx={{ mt: 3 }}>Total: ‚Çπ{total}</Typography>
      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 2 }}
        onClick={() => navigate("/checkout")}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );
}
