import React from "react";

export default function SingleOptionSelector({ title, options, selected, onChange }) {
  return (
    <div style={{ marginBottom: "25px" }}>
      <h4 style={{ textTransform: "capitalize", marginBottom: "10px" }}>{title}</h4>
      <div style={{ display: "flex", gap: "15px", flexWrap: "wrap" }}>
        {options.map((opt) => (
          <button
            key={opt.id}
            onClick={() => onChange(opt.value)}
            style={{
              padding: "10px 14px",
              borderRadius: "8px",
              border: selected === opt.value ? "2px solid #ff4b81" : "1px solid #ccc",
              background: selected === opt.value ? "#ffe6ea" : "#fff",
              cursor: "pointer",
              fontWeight: selected === opt.value ? "600" : "500",
              transition: "0.2s",
            }}
          >
            {opt.value} {opt.extraPrice > 0 ? `(₹${opt.extraPrice})` : ""}
          </button>
        ))}
      </div>
    </div>
  );
}
