// src/pages/CartPage.js
import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  Grid,
  Paper,
  Button,
  TextField
} from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  const fetchCart = () => {
    getCart().then(res => setItems(res.data)).catch(console.error);
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleRemove = (id) => {
    removeFromCart(id).then(fetchCart).catch(console.error);
  };

  const handleQty = (id, qty) => {
    if (qty < 1) return;
    updateCartItem(id, { quantity: qty }).then(fetchCart).catch(console.error);
  };

  // Convert stored data (string/array/object) into readable string
  const toReadable = (data) => {
    if (!data) return "None";

    try {
      // If JSON string like '["1","2"]'
      if (typeof data === "string" && data.startsWith("[")) {
        const arr = JSON.parse(data);
        return arr.join(", ");
      }

      // If object like { size: "Large", shape: "Round" }
      if (typeof data === "object") {
        return Object.entries(data)
          .map(([k, v]) => `${k}: ${v}`)
          .join(", ");
      }

      return data.toString();
    } catch {
      return data.toString();
    }
  };

  const total = items.reduce(
    (sum, it) => sum + (it.totalPrice ?? it.basePrice * (it.quantity || 1)),
    0
  );

  if (!items.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Grid container spacing={2}>
        {items.map((it) => (
          <Grid item xs={12} key={it.id}>
            <Paper
              sx={{
                display: "flex",
                alignItems: "center",
                p: 2,
                borderRadius: 3,
                background: "#fff"
              }}
            >
              {/* Image */}
              <img
                src={`/images/${it.image || it.product?.image}`}
                alt={it.name}
                style={{
                  width: 120,
                  height: 120,
                  objectFit: "cover",
                  borderRadius: 10,
                  marginRight: 18
                }}
              />

              {/* Product Details */}
              <Box sx={{ flex: 1 }}>
                <Typography variant="h6">{it.name}</Typography>

                <Typography variant="body2" sx={{ mt: 1 }}>
                  <strong>Selections:</strong>{" "}
                  {toReadable(it.selectedSingle)}
                </Typography>

                <Typography variant="body2">
                  <strong>Toppings:</strong>{" "}
                  {toReadable(it.selectedToppings)}
                </Typography>
              </Box>

              {/* Qty + Price + Remove */}
              <Box sx={{ textAlign: "right" }}>
                <TextField
                  type="number"
                  value={it.quantity}
                  onChange={(e) =>
                    handleQty(it.id, Math.max(1, parseInt(e.target.value || "1")))
                  }
                  sx={{ width: 80 }}
                  InputProps={{ inputProps: { min: 1 } }}
                />

                <Typography sx={{ mt: 1, fontWeight: "bold" }}>
                  ₹{it.totalPrice ?? it.basePrice * it.quantity}
                </Typography>

                <Button
                  color="error"
                  onClick={() => handleRemove(it.id)}
                  sx={{ mt: 1 }}
                >
                  Remove
                </Button>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h5" sx={{ mt: 3, fontWeight: "bold" }}>
        Total: ₹{total}
      </Typography>

      <Button
        variant="contained"
        fullWidth
        color="primary"
        sx={{ mt: 3, py: 1.5, fontSize: "16px" }}
        onClick={() => navigate("/checkout")}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );
}
