import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, TextField } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

// ID → Label mappings
const OPTION_LABELS = {
size: { 5: "Small", 6: "Medium", 7: "Large" },
flavor: { 8: "Chocolate", 9: "Vanilla", 10: "Strawberry" },
shape: { 2: "Round", 3: "Heart", 8: "Square" },
toppings: {
12: "Choco Chips",
14: "Rainbow Sprinkles",
18: "Nuts",
20: "Fruits",
21: "Cherries"
}
};

// normalize backend values into an array
const toArray = (value) => {
if (!value) return [];
if (Array.isArray(value)) return value;
if (typeof value === "string") {
return value.replace(/[[]\s]/g, "").split(",").filter(Boolean).map(Number);
}
return [];
};

// normalize selectedSingle from backend
const normalizeSelectedSingle = (selectedSingle) => {
if (!selectedSingle) return {};
if (Array.isArray(selectedSingle)) {
// backend sends [{type:"size", id:5}, ...]
return selectedSingle.reduce((acc, cur) => {
acc[cur.type] = cur.id;
return acc;
}, {});
}
return selectedSingle; // assume already an object
};

export default function CartPage() {
const [items, setItems] = useState([]);
const navigate = useNavigate();

useEffect(() => {
let isMounted = true;

```
async function fetchCart() {
  try {
    const response = await getCart();
    if (isMounted) setItems(response.data);
  } catch (err) {
    console.error("Failed to fetch cart:", err);
  }
}

fetchCart();

return () => {
  // cleanup
  isMounted = false;
};
```

}, []);

const handleRemove = async (id) => {
await removeFromCart(id);
const response = await getCart();
setItems(response.data);
};

const handleQty = async (id, qty) => {
if (qty < 1) return;
await updateCartItem(id, { quantity: qty });
const response = await getCart();
setItems(response.data);
};

const total = items.reduce(
(sum, it) => sum + (it.totalPrice ?? it.basePrice * it.quantity),
0
);

if (!items.length)
return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

return (
<Box sx={{ p: 2 }}>
<Typography variant="h4" sx={{ mb: 3 }}>My Cart</Typography>

```
  <Box
    display="grid"
    gap={2}
    sx={{
      gridTemplateColumns: { xs: "1fr", sm: "1fr 1fr", md: "1fr 1fr 1fr" }
    }}
  >
    {items.map((it) => {
      const selected = normalizeSelectedSingle(it.selectedSingle);
      const toppingsArray = toArray(it.selectedToppings);

      return (
        <Paper key={it.id} sx={{ display: "flex", flexDirection: "column", p: 2 }}>
          {/* Image */}
          <img
            src={`/images/${it.image}`}
            alt={it.name}
            style={{ width: "100%", maxWidth: 120, borderRadius: 8, marginBottom: 8 }}
          />

          <Box sx={{ flex: 1 }}>
            <Typography variant="h6">{it.name}</Typography>

            <Typography variant="body2">
              <b>Size:</b> {OPTION_LABELS.size[selected.size] || "None"}
            </Typography>

            <Typography variant="body2">
              <b>Flavor:</b> {OPTION_LABELS.flavor[selected.flavor] || "None"}
            </Typography>

            <Typography variant="body2">
              <b>Shape:</b> {OPTION_LABELS.shape[selected.shape] || "None"}
            </Typography>

            <Typography variant="body2">
              <b>Toppings:</b>{" "}
              {toppingsArray.length
                ? toppingsArray.map((id) => OPTION_LABELS.toppings[id] || id).join(", ")
                : "None"}
            </Typography>
          </Box>

          <Box sx={{ mt: 2, display: "flex", alignItems: "center", justifyContent: "space-between" }}>
            <TextField
              type="number"
              value={it.quantity}
              onChange={(e) =>
                handleQty(it.id, Math.max(1, parseInt(e.target.value || "1")))
              }
              sx={{ width: 80 }}
              InputProps={{ inputProps: { min: 1 } }}
            />

            <Typography>₹{it.totalPrice ?? it.basePrice * it.quantity}</Typography>

            <Button color="error" onClick={() => handleRemove(it.id)}>
              Remove
            </Button>
          </Box>
        </Paper>
      );
    })}
  </Box>

  <Typography variant="h5" sx={{ mt: 3 }}>Total: ₹{total}</Typography>

  <Button
    variant="contained"
    color="primary"
    sx={{ mt: 2 }}
    onClick={() => navigate("/checkout")}
  >
    Proceed to Checkout
  </Button>
</Box>

);
}
