import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, CircularProgress } from "@mui/material";
import { getOrdersByUser } from "../api/orders";

// Safe JSON parse
const safeParse = (val) => {
if (!val) return null;
try { return JSON.parse(val); } catch { return val; }
};

export default function OrdersPage() {
const [orders, setOrders] = useState([]);
const [loading, setLoading] = useState(true);

useEffect(() => {
const username = localStorage.getItem("username");
if (!username) {
setOrders([]);
setLoading(false);
return;
}


getOrdersByUser(username)
  .then((res) => setOrders(res.data))
  .catch(console.error)
  .finally(() => setLoading(false));


}, []);

if (loading)
return <CircularProgress sx={{ mt: 5, display: "block", mx: "auto" }} />;

if (!orders.length)
return <Typography sx={{ p: 3 }}>You have not placed any orders yet.</Typography>;

return (
<Box sx={{ p: 3 }}>
<Typography variant="h4" sx={{ mb: 3 }}>My Orders</Typography> <Grid container spacing={2}>
{orders.map((order) => ( <Grid item xs={12} key={order.id}>
<Paper sx={{ p: 2 }}> <Typography variant="h6">Order ID: {order.id}</Typography> <Typography>User: {order.userName}</Typography> <Typography>Total: ₹{order.totalPrice}</Typography> <Typography>Delivery Date: {new Date(order.deliveryDate).toLocaleDateString()}</Typography> <Typography>Delivery Slot: {order.deliverySlot}</Typography>


          <Box sx={{ mt: 1 }}>
            <Typography variant="subtitle1">Items:</Typography>
            {order.items && order.items.length ? (
              order.items.map((item) => {
                const options = safeParse(item.selectedOptions) || {};
                return (
                  <Paper key={item.id} sx={{ p: 1, mt: 1 }}>
                    <Typography>Name: {item.product?.name ?? item.name}</Typography>
                    <Typography>Quantity: {item.quantity}</Typography>
                    <Typography>Price: ₹{item.finalPrice}</Typography>
                    {Object.keys(options).length ? (
                      <Box>
                        <Typography>Options:</Typography>
                        {Object.entries(options).map(([key, val]) => (
                          <Typography key={key} sx={{ pl: 2 }}>
                            {key}: {Array.isArray(val) ? val.join(", ") : val}
                          </Typography>
                        ))}
                      </Box>
                    ) : (
                      <Typography>Options: None</Typography>
                    )}
                  </Paper>
                );
              })
            ) : (
              <Typography>No items found</Typography>
            )}
          </Box>
        </Paper>
      </Grid>
    ))}
  </Grid>
</Box>


);
}
