import React, { useState, useEffect } from "react";
import axios from "axios";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [userName, setUserName] = useState("mahek"); // change dynamically if needed
  const [deliveryDate, setDeliveryDate] = useState("");
  const [deliverySlot, setDeliverySlot] = useState("10AM-12PM");

  // Fetch cart items from backend (or local state)
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/cart/${userName}`);
        setCart(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchCart();
  }, [userName]);

  const placeOrder = async () => {
    if (!deliveryDate) {
      alert("Please select a delivery date!");
      return;
    }

    const orderData = {
      userName: userName,
      deliveryDate: deliveryDate,
      deliverySlot: deliverySlot
    };

    try {
      const res = await axios.post(
        "http://localhost:8080/api/orders/place",
        orderData
      );
      alert(res.data);
      setCart([]); // clear cart in UI
    } catch (err) {
      console.error(err);
      alert("Failed to place order!");
    }
  };

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>

      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        cart.map((c) => (
          <div key={c.id} className="cart-item">
            <img src={c.image} alt={c.name} width={80} />
            <div>
              <h4>{c.name}</h4>
              <p>Quantity: {c.quantity}</p>
              <p>Price: â‚¹{c.totalPrice}</p>
              {c.selectedSingle && <p>Options: {c.selectedSingle}</p>}
              {c.selectedToppings && <p>Toppings: {c.selectedToppings}</p>}
            </div>
          </div>
        ))
      )}

      {cart.length > 0 && (
        <div className="order-details">
          <label>
            Delivery Date:{" "}
            <input
              type="date"
              value={deliveryDate}
              onChange={(e) => setDeliveryDate(e.target.value)}
            />
          </label>

          <label>
            Delivery Slot:{" "}
            <select
              value={deliverySlot}
              onChange={(e) => setDeliverySlot(e.target.value)}
            >
              <option value="10AM-12PM">10AM-12PM</option>
              <option value="12PM-2PM">12PM-2PM</option>
              <option value="2PM-4PM">2PM-4PM</option>
              <option value="4PM-6PM">4PM-6PM</option>
            </select>
          </label>

          <button onClick={placeOrder}>Place Order</button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
