import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Paper, IconButton } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  // ---------------- LOAD CART ----------------
  const loadCart = async () => {
    try {
      const res = await getCart();
      const data = res.data || [];

      // Ensure toppings always array
      const normalized = data.map((it) => ({
        ...it,
        selectedToppings: Array.isArray(it.selectedToppings)
          ? it.selectedToppings
          : [],
      }));

      setCart(normalized);
    } catch (err) {
      console.error("Error fetching cart:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadCart();
  }, []);

  // ---------------- REMOVE ITEM ----------------
  const handleRemove = async (id) => {
    try {
      await removeFromCart(id);
      loadCart();
    } catch (err) {
      console.error("Error removing item:", err);
    }
  };

  // ---------------- UPDATE QUANTITY ----------------
  const changeQuantity = async (item, newQty) => {
    if (newQty < 1) return;

    try {
      await updateCartItem(item.id, { quantity: newQty });
      loadCart();
    } catch (err) {
      console.error("Error updating item:", err);
    }
  };

  // ---------------- TOTAL PRICE ----------------
  const getItemTotal = (item) => {
    const toppingsPrice = item.selectedToppings.reduce(
      (acc, t) => acc + (t.extraPrice || 0),
      0
    );

    return (item.basePrice + toppingsPrice) * item.quantity;
  };

  const grandTotal = cart.reduce((acc, item) => acc + getItemTotal(item), 0);

  if (loading) {
    return (
      <Typography sx={{ textAlign: "center", mt: 5 }}>
        Loading cart...
      </Typography>
    );
  }

  if (cart.length === 0) {
    return (
      <Typography sx={{ textAlign: "center", mt: 5, fontSize: "1.3rem" }}>
        Your cart is empty!
      </Typography>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        Your Cart
      </Typography>

      <Grid container spacing={2}>
        {cart.map((item) => (
          <Grid item xs={12} key={item.id}>
            <Paper sx={{ p: 2, display: "flex", alignItems: "center" }}>
              
              {/* ---------------- IMAGE ---------------- */}
              <img
                src={`http://localhost:8080/images/${item.image}`}
                alt={item.name}
                style={{
                  width: 120,
                  height: 120,
                  borderRadius: 10,
                  objectFit: "cover",
                  marginRight: 20,
                }}
                onError={(e) => {
                  e.target.src =
                    "https://via.placeholder.com/120?text=No+Image";
                }}
              />

              {/* ---------------- DETAILS ---------------- */}
              <Box sx={{ flexGrow: 1 }}>
                <Typography variant="h6">{item.name}</Typography>

                <Typography>
                  <strong>Shape:</strong> {item.selectedShape}
                </Typography>

                {item.selectedToppings.length > 0 && (
                  <Typography>
                    <strong>Toppings:</strong>{" "}
                    {item.selectedToppings.map((t) => t.name).join(", ")}
                  </Typography>
                )}

                <Typography>
                  <strong>Base Price:</strong> ₹{item.basePrice}
                </Typography>

                {/* ---------------- QUANTITY ---------------- */}
                <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
                  <Button
                    variant="contained"
                    onClick={() => changeQuantity(item, item.quantity - 1)}
                  >
                    -
                  </Button>

                  <Typography sx={{ mx: 2 }}>{item.quantity}</Typography>

                  <Button
                    variant="contained"
                    onClick={() => changeQuantity(item, item.quantity + 1)}
                  >
                    +
                  </Button>
                </Box>

                <Typography sx={{ mt: 1, fontWeight: "bold" }}>
                  Item Total: ₹{getItemTotal(item)}
                </Typography>
              </Box>

              {/* ---------------- REMOVE BUTTON ---------------- */}
              <IconButton
                color="error"
                onClick={() => handleRemove(item.id)}
              >
                <DeleteIcon />
              </IconButton>
            </Paper>
          </Grid>
        ))}
      </Grid>

      {/* ---------------- FOOTER TOTAL + CHECKOUT ---------------- */}
      <Box sx={{ mt: 4, textAlign: "right" }}>
        <Typography variant="h5" sx={{ mb: 2 }}>
          Grand Total: ₹{grandTotal}
        </Typography>

        <Button
          variant="contained"
          size="large"
          onClick={() => navigate("/placeorder")}
        >
          Proceed to Checkout
        </Button>
      </Box>
    </Box>
  );
}
