// src/pages/Checkout.js
import React, { useEffect, useState } from "react";
import { Box, Typography, TextField, Button, MenuItem } from "@mui/material";
import { getCart } from "../api/cart";
import { placeOrder } from "../api/orders";
import { useNavigate } from "react-router-dom";
import dayjs from "dayjs";

export default function Checkout() {
  const [cart, setCart] = useState([]);
  const [address, setAddress] = useState("");
  const [deliveryDate, setDeliveryDate] = useState(dayjs().format("YYYY-MM-DD"));
  const [deliverySlot, setDeliverySlot] = useState("Morning");

  const navigate = useNavigate();

  // Check if user is logged in
  const username = localStorage.getItem("username");
  useEffect(() => {
    if (!username) {
      navigate("/login"); // redirect to login if not logged in
    }
  }, [username, navigate]);

  // Fetch cart
  useEffect(() => {
    getCart()
      .then((res) => setCart(res.data))
      .catch(console.error);
  }, []);

  // Calculate total price
  const total = cart.reduce(
    (sum, it) => sum + (it.totalPrice ?? it.basePrice * (it.quantity || 1)),
    0
  );

  // Handle order submission
  const submit = () => {
    if (!address) return alert("Please fill your address");

    const orderPayload = {
      userName: username, // use logged-in username
      deliveryDate: new Date(deliveryDate),
      deliverySlot,
      items: cart.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
        selectedSingle: JSON.stringify(item.selectedSingle || {}),
        selectedToppings: JSON.stringify(item.selectedToppings || []),
        totalPrice: item.totalPrice ?? item.basePrice * item.quantity
      }))
    };

    placeOrder(orderPayload)
      .then(() => {
        alert("Order placed successfully!");
        navigate("/orders"); // go to user's order page
      })
      .catch(console.error);
  };

  if (!cart.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ maxWidth: 600, mx: "auto", p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        Checkout
      </Typography>

      <TextField
        label="Username"
        fullWidth
        value={username || ""}
        disabled
        sx={{ mb: 2 }}
      />

      <TextField
        label="Address"
        fullWidth
        multiline
        rows={4}
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        sx={{ mb: 2 }}
      />

      <TextField
        label="Delivery Date"
        type="date"
        fullWidth
        value={deliveryDate}
        onChange={(e) => setDeliveryDate(e.target.value)}
        sx={{ mb: 2 }}
        InputLabelProps={{ shrink: true }}
      />

      <TextField
        select
        label="Delivery Slot"
        fullWidth
        value={deliverySlot}
        onChange={(e) => setDeliverySlot(e.target.value)}
        sx={{ mb: 2 }}
      >
        <MenuItem value="Morning">Morning (8 AM - 12 PM)</MenuItem>
        <MenuItem value="Afternoon">Afternoon (12 PM - 4 PM)</MenuItem>
        <MenuItem value="Evening">Evening (4 PM - 8 PM)</MenuItem>
      </TextField>

      <Typography variant="h6" sx={{ mt: 2 }}>
        Total: â‚¹{total}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 3 }}
        onClick={submit}
      >
        Place Order
      </Button>
    </Box>
  );
}
