import React from "react";
import {
  FormControl,
  FormLabel,
  FormGroup,
  FormControlLabel,
  Checkbox
} from "@mui/material";

export default function ToppingsSelector({ title, options = [], selected = [], onChange }) {

  const normalizeLabel = (opt) => {
    const displayName = opt.value || opt.name;
    const price = opt.extraPrice ?? opt.extra_price ?? 0;
    return price ? `${displayName} (+₹${price})` : displayName;
  };

  // normalize selected IDs to strings for comparison
  const selectedIds = selected.map(String);

  const handleToggle = (id) => {
    const updated = selectedIds.includes(id)
      ? selectedIds.filter(x => x !== id)
      : [...selectedIds, id];

    // convert back to numbers before sending to backend/state
    onChange(updated.map(Number));
  };

  return (
    <FormControl component="fieldset" sx={{ mt: 3 }}>
      <FormLabel component="legend" sx={{ mb: 1, fontWeight: 600 }}>
        {title}
      </FormLabel>

      <FormGroup>
        {options.map((opt) => (
          <FormControlLabel
            key={opt.id}
            control={
              <Checkbox
                checked={selectedIds.includes(opt.id.toString())}
                onChange={() => handleToggle(opt.id.toString())}
              />
            }
            label={normalizeLabel(opt)}
          />
        ))}
      </FormGroup>
    </FormControl>
  );
}
