import React, { useState } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import { useNavigate, Link } from "react-router-dom";

export default function Register({ onRegister }) {
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const navigate = useNavigate();

const handleSubmit = () => {
if (!username || !password) return alert("Please enter username and password");


const users = JSON.parse(localStorage.getItem("users") || "[]");
if (users.find(u => u.username === username)) return alert("Username already exists");

const newUser = { username, password };
users.push(newUser);
localStorage.setItem("users", JSON.stringify(users));

onRegister && onRegister(newUser);

alert("Registration successful! Please login."); // Inform user
navigate("/login"); // Redirect to login page

};

return (
<Box sx={{ maxWidth: 400, mx: "auto", mt: 5 }}>
<Typography variant="h4" sx={{ mb: 3 }}>Register</Typography>
<TextField
label="Username"
fullWidth
value={username}
onChange={(e) => setUsername(e.target.value)}
sx={{ mb: 2 }}
/>
<TextField
label="Password"
type="password"
fullWidth
value={password}
onChange={(e) => setPassword(e.target.value)}
sx={{ mb: 2 }}
/> <Button
     variant="contained"
     color="primary"
     fullWidth
     onClick={handleSubmit}
   >
Register </Button>

