import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, TextField } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

// ðŸ“Œ Mapping based on your REAL database IDs
const OPTION_LABELS = {
  size: {
    4: "Small",
    5: "Medium",
    6: "Large",
  },
  flavor: {
    1: "Chocolate",
    2: "Vanilla",
    3: "Strawberry",
  },
  shape: {
    7: "Round",
    8: "Heart",
    9: "Square",
  },
  toppings: {
    10: "Choco Chips",
    11: "Rainbow Sprinkler",
    12: "Nuts",
    13: "Fruits",
    14: "Cherries",
  },
};

// Parse JSON safely
const safeParse = (val) => {
  if (!val) return null;
  try {
    return JSON.parse(val);
  } catch {
    return null;
  }
};

export default function CartPage() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    load();
  }, []);

  const load = async () => {
    const res = await getCart();
    setItems(res.data);
  };

  const removeItem = async (id) => {
    await removeFromCart(id);
    load();
  };

  const updateQty = async (id, qty) => {
    if (qty < 1) return;
    await updateCartItem(id, { quantity: qty });
    load();
  };

  const totalPrice = items.reduce(
    (sum, it) => sum + (it.totalPrice ?? it.basePrice * it.quantity),
    0
  );

  if (!items.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Box display="grid" gap={2}>
        {items.map((it) => {
          // Parse strings into objects/arrays
          const selected = safeParse(it.selectedSingle) || {};
          const toppings = safeParse(it.selectedToppings) || [];

          return (
            <Paper key={it.id} sx={{ display: "flex", p: 2 }}>
              {/* Product Image */}
              <img
                src={`/images/${it.image}`}
                alt={it.name}
                style={{
                  width: 120,
                  borderRadius: 8,
                  marginRight: 16,
                  objectFit: "cover",
                }}
              />

              <Box sx={{ flex: 1 }}>
                <Typography variant="h6">{it.name}</Typography>

                {/* Size */}
                <Typography variant="body2">
                  <b>Size:</b> {OPTION_LABELS.size[selected.size] ?? "None"}
                </Typography>

                {/* Flavor */}
                <Typography variant="body2">
                  <b>Flavor:</b> {OPTION_LABELS.flavor[selected.flavor] ?? "None"}
                </Typography>

                {/* Shape */}
                <Typography variant="body2">
                  <b>Shape:</b> {OPTION_LABELS.shape[selected.shape] ?? "None"}
                </Typography>

                {/* Toppings */}
                <Typography variant="body2">
                  <b>Toppings:</b>{" "}
                  {toppings.length
                    ? toppings.map((id) => OPTION_LABELS.toppings[id]).join(", ")
                    : "None"}
                </Typography>
              </Box>

              <Box sx={{ textAlign: "right" }}>
                <TextField
                  type="number"
                  value={it.quantity}
                  onChange={(e) =>
                    updateQty(it.id, Math.max(1, Number(e.target.value)))
                  }
                  sx={{ width: 80, mb: 1 }}
                  InputProps={{ inputProps: { min: 1 } }}
                />

                <Typography>
                  â‚¹{it.totalPrice ?? it.basePrice * it.quantity}
                </Typography>

                <Button color="error" onClick={() => removeItem(it.id)}>
                  Remove
                </Button>
              </Box>
            </Paper>
          );
        })}
      </Box>

      <Typography variant="h5" sx={{ mt: 3 }}>
        Total: â‚¹{totalPrice}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 2 }}
        onClick={() => navigate("/checkout")}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );
}
