import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, CircularProgress } from "@mui/material";
import { getAllOrders } from "../api/orders";

// 1. Define the Option Mappings (IDs to Names)
const OPTION_LABELS = {
    size: { 4: "Small", 5: "Medium", 6: "Large" },
    flavor: { 1: "Chocolate", 2: "Vanilla", 3: "Strawberry" },
    shape: { 7: "Round", 8: "Heart", 9: "Square" },
    toppings: { 10: "Choco Chips", 11: "Rainbow Sprinkler", 12: "Nuts", 13: "Fruits", 14: "Cherries" },
};

// 2. Safe JSON parse function
// Handles both nested ({selectedSingle: {}, selectedToppings: []}) and flat ({size: 5, toppings: []}) structures.
const safeParse = (val) => {
    if (!val) return null;
    try {
        const parsed = JSON.parse(val);

        // Case A: Nested Structure (e.g., from Cart Page or recent saves)
        if (parsed.selectedSingle) {
            return {
                single: parsed.selectedSingle,
                toppings: parsed.selectedToppings,
            };
        }
        
        // Case B: Flat Structure (e.g., older saves)
        return {
            // Filter to include only keys that are NOT 'toppings' AND whose values are numeric IDs.
            single: Object.fromEntries(
                Object.entries(parsed).filter(([key, value]) => 
                    key !== 'toppings' && typeof value === 'number'
                )
            ),
            // Ensure toppings is an array
            toppings: Array.isArray(parsed.toppings) ? parsed.toppings : [],
        };
    } catch {
        return null;
    }
};

export default function OrdersPage() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // FIX: Declare 'username' outside try/catch to resolve the "no-undef" error.
        let username; 
        
        const currentUserJSON = localStorage.getItem("currentUser");
        if (!currentUserJSON) {
            setOrders([]);
            setLoading(false);
            return;
        }

        try {
            const currentUser = JSON.parse(currentUserJSON);
            username = currentUser.username;
        } catch (e) {
            setOrders([]);
            setLoading(false);
            return;
        }

        if (!username) {
            setOrders([]);
            setLoading(false);
            return;
        }

        getAllOrders()
            .then((res) => {
                // Filter orders by logged-in user (case-insensitive comparison)
                const userOrders = res.data.filter(
                    (order) => order.userName.toLowerCase() === username.toLowerCase()
                );
                setOrders(userOrders);
            })
            .catch(console.error)
            .finally(() => setLoading(false));
    }, []);

    if (loading)
        return <CircularProgress sx={{ mt: 5, display: "block", mx: "auto" }} />;

    if (!orders.length)
        return <Typography sx={{ p: 3 }}>You have not placed any orders yet.</Typography>;

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h4" sx={{ mb: 3 }}>
                My Orders
            </Typography>

            <Grid container spacing={2}>
                {orders.map((order) => (
                    <Grid item xs={12} sm={6} md={4} key={order.id}>
                        <Paper sx={{ p: 2, height: '100%' }}>
                            <Typography variant="h6" color="primary">Order ID: {order.id}</Typography>
                            <Typography>User: {order.userName}</Typography>
                            <Typography>Total: **₹{order.totalPrice}**</Typography>
                            <Typography>
                                Delivery Date: {new Date(order.deliveryDate).toLocaleDateString()}
                            </Typography>
                            <Typography sx={{ mb: 2 }}>Delivery Slot: {order.deliverySlot}</Typography>
                            
                            <hr />

                            <Box sx={{ mt: 2 }}>
                                <Typography variant="subtitle1" fontWeight="bold">Items:</Typography>

                                {order.items && order.items.length ? (
                                    order.items.map((item, index) => {
                                        const optionsData = safeParse(item.selectedOptions) || {};
                                        // The structure is guaranteed to be {single: {size: 5}, toppings: [10, 11]}
                                        const { single = {}, toppings = [] } = optionsData; 
                                        
                                        // Prepare Topping Names
                                        const toppingNames = toppings
                                            .map(id => OPTION_LABELS.toppings[id])
                                            .filter(Boolean)
                                            .join(", ");
                                        
                                        const hasOptions = Object.keys(single).length > 0 || toppings.length > 0;

                                        return (
                                            <Paper key={item.id} sx={{ p: 1, mt: 1, border: '1px solid #eee' }}>
                                                <Typography>Name: **{item.product?.name ?? item.name}**</Typography>
                                                <Typography>Quantity: {item.quantity}</Typography>
                                                <Typography>Price: ₹{item.finalPrice}</Typography>

                                                <Box sx={{ mt: 1 }}>
                                                    <Typography variant="subtitle2" color="textSecondary">Custom Options:</Typography>
                                                    
                                                    {hasOptions ? (
                                                        <>
                                                            {/* Display Size, Flavor, and Shape Individually */}
                                                            {Object.entries(single).map(([key, id]) => (
                                                                <Typography key={key} sx={{ pl: 2, fontSize: '0.9rem' }}>
                                                                    **{key.charAt(0).toUpperCase() + key.slice(1)}**: {OPTION_LABELS[key]?.[id] ?? 'N/A'}
                                                                </Typography>
                                                            ))}
                                                            
                                                            {/* Display Toppings */}
                                                            {toppingNames.length > 0 && (
                                                                <Typography sx={{ pl: 2, fontSize: '0.9rem' }}>
                                                                    **Toppings**: {toppingNames}
                                                                </Typography>
                                                            )}
                                                        </>
                                                    ) : (
                                                        <Typography sx={{ pl: 2, fontSize: '0.9rem' }}>None</Typography>
                                                    )}
                                                </Box>
                                            </Paper>
                                        );
                                    })
                                ) : (
                                    <Typography>No items found</Typography>
                                )}
                            </Box>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
        </Box>
    );
}
