import React, { useEffect, useState } from "react";
import { Box, Typography, TextField, Button, Grid } from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
import { getProductById } from "../api/Products";
import { calculatePrice } from "../api/Price";
import { addToCart } from "../api/Cart";
import SingleOptionSelector from "../components/Customizer/SingleOptionSelector";
import ToppingsSelector from "../components/Customizer/ToppingsSelector";
import PriceBox from "../components/Customizer/PriceBox";

const CustomizerPage = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState({});
  const [selectedSingle, setSelectedSingle] = useState({});
  const [selectedToppings, setSelectedToppings] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    getProductById(productId).then(res => setProduct(res.data)).catch(err => console.error(err));
  }, [productId]);

  useEffect(() => {
    if (!product) return;
    calculatePrice({ basePrice: product.basePrice, selectedSingle, selectedToppings, quantity })
      .then(res => setTotalPrice(res.data.total))
      .catch(err => console.error(err));
  }, [product, selectedSingle, selectedToppings, quantity]);

  const handleAddToCart = () => {
    addToCart({ productId: product.id, name: product.name, image: product.image, basePrice: product.basePrice, selectedSingle, selectedToppings, quantity, totalPrice })
      .then(() => {
        alert("Added to cart!");
        navigate("/cart");
      })
      .catch(err => console.error(err));
  };

  if (!product) return <Typography>Loading...</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4">{product.name}</Typography>
      <img src={product.image} alt={product.name} style={{ width: "300px", borderRadius: "10px" }} />

      <Box mt={3}>
        {["flavor", "size", "shape", "decoration"].map(type => (
          <SingleOptionSelector
            key={type}
            title={type}
            options={options[type] || []}
            selected={selectedSingle[type] || ""}
            onChange={value => setSelectedSingle(prev => ({ ...prev, [type]: value }))}
          />
        ))}
        <ToppingsSelector options={options["topping"] || []} selected={selectedToppings} onChange={setSelectedToppings} />
      </Box>

      <Box mt={2}>
        <TextField
          type="number"
          label="Quantity"
          value={quantity}
          onChange={e => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
          InputProps={{ inputProps: { min: 1 } }}
        />
      </Box>

      <PriceBox price={totalPrice} />

      <Button variant="contained" color="secondary" sx={{ mt: 2 }} onClick={handleAddToCart}>
        Add to Cart
      </Button>
    </Box>
  );
};

export default CustomizerPage;
