import React from "react";
import { Card, CardMedia, CardContent, Typography, CardActions, Button } from "@mui/material";
import { productImagePath } from "../utils/imageHelpers";

export default function ProductCard({ product, onCustomize, categoryId }) {
  const img = productImagePath(product);
  const isPastry = categoryId === 7; // reliable check

  return (
    <Card sx={{ height: "100%", display: "flex", flexDirection: "column" }}>
      <CardMedia component="img" height="180" image={img} alt={product.name} />
      <CardContent sx={{ flexGrow: 1 }}>
        <Typography variant="h6">{product.name}</Typography>

        {isPastry && (
          <Typography variant="body2" color="text.secondary" sx={{ fontStyle: 'italic' }}>
            Slice
          </Typography>
        )}

        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          {product.description}
        </Typography>
        <Typography variant="subtitle1" sx={{ mt: 1, fontWeight: 700 }}>
          â‚¹{product.base_price ?? product.basePrice}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" variant="contained" onClick={() => onCustomize(product.id)}>Customize</Button>
        <Button size="small" component="a" href={`/product/${product.id}`}>View</Button>
      </CardActions>
    </Card>
  );
}
