// src/pages/AdminDashboard.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Grid, Button } from "@mui/material";

export default function AdminDashboard() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const allOrders = JSON.parse(localStorage.getItem("orders") || "[]");
    setOrders(allOrders);
  }, []);

  const updateStatus = (id, status) => {
    const updated = orders.map(o => o.id === id ? { ...o, status } : o);
    setOrders(updated);
    localStorage.setItem("orders", JSON.stringify(updated));
  };

  if (!orders.length) return <Typography sx={{ p: 3 }}>No orders yet.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>Admin Dashboard</Typography>
      <Grid container spacing={2}>
        {orders.map(order => (
          <Grid item xs={12} key={order.id}>
            <Paper sx={{ p: 2 }}>
              <Typography><b>Order ID:</b> {order.id}</Typography>
              <Typography><b>User:</b> {order.userName}</Typography>
              <Typography><b>Total:</b> â‚¹{order.totalPrice}</Typography>
              <Typography><b>Status:</b> {order.status}</Typography>
              <Box sx={{ mt: 1 }}>
                <Button sx={{ mr: 1 }} variant="contained" onClick={() => updateStatus(order.id, "Pending")}>Pending</Button>
                <Button sx={{ mr: 1 }} variant="contained" color="success" onClick={() => updateStatus(order.id, "Completed")}>Completed</Button>
                <Button variant="contained" color="error" onClick={() => updateStatus(order.id, "Cancelled")}>Cancelled</Button>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
