import axios from "axios";
const BASE = "http://localhost:8080/api/cart";

export const getCart = () => axios.get(`${BASE}`);
export const addToCart = (item) => axios.post(`${BASE}`, item);
export const removeFromCart = (id) => axios.delete(`${BASE}/${id}`);
export const updateCartItem = (id, data) => axios.put(`${BASE}/${id}`, data);
