// src/pages/AdminDashboard.js (FINALIZED)
import React, { useEffect, useState } from "react";
import axios from "axios";
import {
    Box,
    Typography,
    Paper,
    TextField,
    Button,
    Divider,
    Stack, // Added Stack for better button layout
} from "@mui/material";
import { CheckCircle as CheckCircleIcon, LocalShipping as LocalShippingIcon } from '@mui/icons-material';

// --- Helper for Status Display (Used in both Admin and Orders page) ---
const getStatusDisplay = (status) => {
    switch (status?.toLowerCase()) {
        case 'delivered':
            return {
                icon: <CheckCircleIcon sx={{ color: '#2e7d32', mr: 1 }} />, // Green
                color: '#2e7d32',
                text: 'Delivered',
            };
        case 'shipped':
        case 'out for delivery':
            return {
                icon: <LocalShippingIcon sx={{ color: '#ff9800', mr: 1 }} />, // Orange/Amber
                color: '#ff9800',
                text: 'Shipped / Out for Delivery',
            };
        default:
            return {
                icon: null,
                color: '#A00045', // Dark Pink/Berry
                text: status || 'Processing',
            };
    }
};
// -------------------------------------------------------------------

export default function AdminDashboard() {
    const [orders, setOrders] = useState([]);
    // State to hold user input for NEW tracking messages, initialized empty.
    const [trackingMsgs, setTrackingMsgs] = useState({}); 

    // Fetch all orders
    const fetchOrders = async () => {
        try {
            // This API call is assumed to return the order object with 'status' and 'trackingMessage'.
            const res = await axios.get("http://localhost:8080/api/orders/all");
            setOrders(res.data);
            
            // NOTE: We DO NOT initialize trackingMsgs state here. 
            // The TextField should remain empty for a NEW message, 
            // while the CURRENT tracking message is displayed directly from the 'order' object.

        } catch (err) {
            console.error("Failed to fetch orders:", err);
        }
    };

    useEffect(() => {
        fetchOrders();
    }, []);

    // Update order status
    const handleStatusUpdate = async (id, status) => {
        try {
            await axios.put(
                `http://localhost:8080/api/orders/status/${id}?status=${status}`
            );
            // Re-fetch orders to reflect the status change immediately
            fetchOrders(); 
        } catch (err) {
            console.error("Failed to update status:", err);
        }
    };

    // Send tracking message
    const handleTrackingMsg = async (id) => {
        try {
            const msg = trackingMsgs[id] || "";
            if (!msg.trim()) {
                alert("Please enter a tracking message.");
                return;
            }
            await axios.put(
                `http://localhost:8080/api/orders/track/${id}?msg=${encodeURIComponent(
                    msg
                )}`
            );
            alert("Tracking message sent!");
            // Clear the input field for the next message
            setTrackingMsgs((prev) => {
                const newMsgs = { ...prev };
                delete newMsgs[id];
                return newMsgs;
            });
            // Re-fetch orders to reflect the saved message immediately
            fetchOrders(); 
        } catch (err) {
            console.error("Failed to send tracking message:", err);
        }
    };

    const handleInputChange = (id, value) => {
        setTrackingMsgs({ ...trackingMsgs, [id]: value });
    };

    return (
        <Box sx={{ mt: 4, p: 3, maxWidth: 1200, mx: 'auto' }}>
            <Typography variant="h4" sx={{ mb: 4, fontWeight: 'bold' }}>
                ðŸšš Admin Dashboard â€” All Orders
            </Typography>

            {orders.length === 0 && <Typography>No orders found.</Typography>}

            <Stack spacing={4}>
                {orders.map((order) => {
                    const statusDisplay = getStatusDisplay(order.status);
                    
                    return (
                        <Paper key={order.id} sx={{ p: 3, border: `1px solid #ddd` }}>
                            {/* --- HEADER: ID, USER, and CURRENT STATUS --- */}
                            <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
                                <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
                                    Order ID: {order.id} | User: {order.userName}
                                </Typography>
                                <Box display="flex" alignItems="center" sx={{ 
                                    p: 0.5, 
                                    borderRadius: 1, 
                                    bgcolor: statusDisplay.color ? `${statusDisplay.color}10` : '#eee' // Light background for status
                                }}>
                                    {statusDisplay.icon}
                                    <Typography sx={{ color: statusDisplay.color, fontWeight: 'bold' }}>
                                        {statusDisplay.text}
                                    </Typography>
                                </Box>
                            </Box>

                            <Typography>
                                <b>Total:</b> â‚¹{order.totalPrice}
                                &nbsp;|&nbsp;
                                <b>Delivery:</b> {new Date(order.deliveryDate).toLocaleDateString()} ({order.deliverySlot})
                            </Typography>
                            
                            {/* --- LAST SENT TRACKING MESSAGE --- */}
                            {order.trackingMessage && (
                                <Box sx={{ mt: 1, p: 1, borderLeft: '4px solid #faadd3ff', bgcolor: '#fdf3f8' }}>
                                    <Typography variant="body2" sx={{ fontStyle: 'italic', color: 'text.secondary' }}>
                                        **Last Message Sent:** {order.trackingMessage}
                                    </Typography>
                                </Box>
                            )}

                            <Divider sx={{ my: 2 }} />

                            {/* --- ITEMS LIST --- */}
                            <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold' }}>
                                Items:
                            </Typography>
                            <Stack spacing={1} sx={{ pl: 2 }}>
                                {order.items?.map((item) => (
                                    <Box key={item.id} sx={{ display: 'flex', gap: 2, alignItems: 'center', borderBottom: '1px dotted #eee', pb: 1 }}>
                                        {/* Using a simple img tag for brevity, assuming URL is present */}
                                        {item.product?.imageUrl && (
                                            <img
                                                src={item.product.imageUrl}
                                                alt={item.product?.name}
                                                style={{ width: 60, height: 60, objectFit: "cover", borderRadius: 4 }}
                                            />
                                        )}
                                        <Box>
                                            <Typography>
                                                <b>{item.product?.name}</b>
                                            </Typography>
                                            <Typography variant="body2">
                                                Qty: {item.quantity} | Price: â‚¹{item.finalPrice}
                                            </Typography>
                                        </Box>
                                    </Box>
                                ))}
                            </Stack>

                            <Divider sx={{ my: 2 }} />

                            {/* --- ACTION SECTION --- */}
                            <Typography variant="subtitle1" sx={{ mb: 1, fontWeight: 'bold' }}>
                                Admin Actions:
                            </Typography>
                            <TextField
                                label="New Tracking Message for User"
                                fullWidth
                                multiline
                                rows={2}
                                sx={{ mb: 2 }}
                                value={trackingMsgs[order.id] || ""}
                                onChange={(e) => handleInputChange(order.id, e.target.value)}
                            />

                            <Box display="flex" gap={1}>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={() => handleTrackingMsg(order.id)}
                                >
                                    Send Tracking Message
                                </Button>

                                {/* Optionally add a 'Ship' status before 'Delivered' */}
                                {order.status?.toLowerCase() !== 'delivered' && (
                                    <Button
                                        variant="outlined"
                                        color="warning"
                                        onClick={() => handleStatusUpdate(order.id, "Shipped")}
                                    >
                                        Mark Shipped
                                    </Button>
                                )}

                                <Button
                                    variant="contained"
                                    color="success"
                                    onClick={() => handleStatusUpdate(order.id, "Delivered")}
                                    disabled={order.status?.toLowerCase() === 'delivered'}
                                >
                                    Mark Delivered
                                </Button>
                            </Box>
                        </Paper>
                    );
                })}
            </Stack>
        </Box>
    );
}
