// api/products.js
import axios from "axios";

export const getAllProducts = () => axios.get("http://localhost:8080/api/products");
export const getProductById = (id) => axios.get(`http://localhost:8080/api/products/${id}`);

// Add these for CustomizerPage
export const getAllOptions = () =>
  axios.get("http://localhost:8080/api/options"); // backend endpoint for product options

export const calculatePrice = ({ productId, selectedOptions }) =>
  axios.post("http://localhost:8080/api/products/calculate", {
    productId,
    options: selectedOptions,
  });
