// src/pages/CategoryProducts.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, CircularProgress } from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import ProductCard from "../components/ProductCard";

const CategoryProducts = () => {
  const { categoryId } = useParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:8080/api/categories/${categoryId}/products`)
      .then(res => setProducts(res.data))
      .catch(err => console.error(err))
      .finally(() => setLoading(false));
  }, [categoryId]);

  const handleCustomize = (productId) => {
    navigate(`/customize/${productId}`);
  };

  if (loading) return <Box sx={{ textAlign: "center", mt: 5 }}><CircularProgress /></Box>;
  if (!products.length) return <Typography sx={{ p: 3 }}>No products found in this category.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>Products in this Category</Typography>
      <Grid container spacing={2}>
        {products.map(product => (
          <Grid item xs={12} sm={6} md={3} key={product.id}>
            <ProductCard product={product} onCustomize={handleCustomize} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default CategoryProducts;
