// src/pages/LoginPage.js
import React, { useState } from "react";
import { Box, TextField, Button, Typography, Paper, MenuItem, Link } from "@mui/material";
import { useNavigate, useLocation } from "react-router-dom";

export default function LoginPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const [username, setUsername] = useState("");
  const [role, setRole] = useState("user");
  const [error, setError] = useState("");

  // Read redirect path if any
  const redirectAfterLogin = new URLSearchParams(location.search).get("redirect") || null;

  const handleLogin = () => {
    if (!username) {
      setError("Username is required");
      return;
    }

    localStorage.setItem("username", username);
    localStorage.setItem("role", role);

    if (redirectAfterLogin) navigate(redirectAfterLogin);
    else navigate(role === "admin" ? "/admin/orders" : "/");
  };

  return (
    <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100vh", bgcolor: "#f5f5f5" }}>
      <Paper sx={{ p: 4, width: 400 }}>
        <Typography variant="h5" sx={{ mb: 3, textAlign: "center" }}>Login</Typography>

        <TextField
          label="Username"
          fullWidth
          sx={{ mb: 2 }}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <TextField
          select
          label="Role"
          fullWidth
          sx={{ mb: 2 }}
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <MenuItem value="user">User</MenuItem>
          <MenuItem value="admin">Admin</MenuItem>
        </TextField>

        {error && <Typography color="error" sx={{ mb: 2 }}>{error}</Typography>}

        <Button variant="contained" fullWidth onClick={handleLogin}>Login</Button>

        <Typography sx={{ mt: 2, textAlign: "center" }}>
          Don't have an account?{" "}
          <Link component="button" variant="body2" onClick={() => navigate("/register")}>
            Register
          </Link>
        </Typography>
      </Paper>
    </Box>
  );
}
