// src/pages/CartPage.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, Grid, CircularProgress } from "@mui/material";
import { getCart, removeFromCart } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    loadCart();
  }, []);

  const loadCart = () => {
    setLoading(true);

    getCart()
      .then((res) => {
        const fixed = Array.isArray(res.data) ? res.data : [];
        setCart(fixed);
      })
      .catch(() => setCart([]))
      .finally(() => setLoading(false));
  };

  // Safely convert toppings to array
  const safeToppings = (t) => {
    if (Array.isArray(t)) return t;

    if (typeof t === "string") {
      return t
        .replace(/[\[\]]/g, "")
        .split(",")
        .map((x) => x.trim())
        .filter(Boolean);
    }

    return [];
  };

  if (loading)
    return <CircularProgress sx={{ mt: 5, display: "block", mx: "auto" }} />;

  if (!cart.length)
    return (
      <Typography sx={{ p: 3, textAlign: "center" }}>
        Your cart is empty.
      </Typography>
    );

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Grid container spacing={2}>
        {cart.map((item) => (
          <Grid item xs={12} key={item.id}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6">{item.productName}</Typography>
              <Typography>Quantity: {item.quantity}</Typography>
              <Typography>Total Price: â‚¹{item.totalPrice}</Typography>

              <Box sx={{ mt: 1 }}>
                <Typography variant="subtitle2">Selected Options:</Typography>

                <Typography>
                  Shape: {item.selectedSingle ? item.selectedSingle : "None"}
                </Typography>

                <Typography>
                  Toppings:{" "}
                  {safeToppings(item.selectedToppings).length
                    ? safeToppings(item.selectedToppings).join(", ")
                    : "None"}
                </Typography>
              </Box>

              <Button
                variant="contained"
                color="error"
                sx={{ mt: 2 }}
                onClick={() =>
                  removeFromCart(item.id).then(() => loadCart())
                }
              >
                Remove
              </Button>
            </Paper>
          </Grid>
        ))}
      </Grid>

      <Button
        variant="contained"
        sx={{ mt: 3 }}
        onClick={() => navigate("/checkout")}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );
}
