// src/pages/Home.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Grid } from "@mui/material";
import axios from "axios";
import CategoryCard from "../components/CategoryCard";
import ProductCard from "../components/ProductCard";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch categories
    axios.get("http://localhost:8080/api/categories")
      .then(res => setCategories(res.data))
      .catch(err => console.error(err));

    // Fetch products
    axios.get("http://localhost:8080/api/products")
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleCategorySelect = (categoryId) => {
    navigate(`/category/${categoryId}`);
  };

  const handleCustomize = (productId) => {
    navigate(`/customize/${productId}`);
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>Categories</Typography>
      <Grid container spacing={2}>
        {categories.map(category => (
          <Grid item xs={12} sm={6} md={3} key={category.id}>
            <CategoryCard category={category} onSelect={handleCategorySelect} />
          </Grid>
        ))}
      </Grid>

      <Typography variant="h4" gutterBottom sx={{ mt: 5 }}>Products</Typography>
      <Grid container spacing={2}>
        {products.map(product => (
          <Grid item xs={12} sm={6} md={3} key={product.id}>
            <ProductCard product={product} onCustomize={handleCustomize} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Home;
