import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

const Cart = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);

  // Load cart from backend or localStorage
  useEffect(() => {
    // Example: fetch from backend
    axios.get("http://localhost:8080/api/cart")
      .then(res => setCartItems(res.data))
      .catch(err => console.log(err));
  }, []);

  // Calculate total price
  useEffect(() => {
    const sum = cartItems.reduce((acc, item) => acc + item.totalPrice, 0);
    setTotal(sum);
  }, [cartItems]);

  const handleQuantityChange = (index, quantity) => {
    const updated = [...cartItems];
    updated[index].quantity = quantity;

    // Optionally recalc totalPrice via API
    axios.post("http://localhost:8080/api/price/calculate", {
      basePrice: updated[index].basePrice,
      selectedSingle: updated[index].selectedSingle,
      selectedToppings: updated[index].selectedToppings,
      quantity
    }).then(res => {
      updated[index].totalPrice = res.data.total;
      setCartItems(updated);
    });
  };

  const handleRemove = (index) => {
    const updated = [...cartItems];
    const removedItem = updated.splice(index, 1);
    setCartItems(updated);

    // Optionally delete from backend
    axios.delete(`http://localhost:8080/api/cart/${removedItem[0].id}`)
      .catch(err => console.log(err));
  };

  const goToCheckout = () => {
    navigate("/checkout");
  };

  if (cartItems.length === 0) return <p style={{ padding: "40px" }}>Your cart is empty.</p>;

  return (
    <div style={{ padding: "40px" }}>
      <h2>My Cart</h2>
      {cartItems.map((item, index) => (
        <div key={index} style={{ display: "flex", alignItems: "center", marginBottom: "20px", border: "1px solid #eee", padding: "10px", borderRadius: "10px" }}>
          <img src={item.image} alt={item.name} style={{ width: "120px", borderRadius: "10px", marginRight: "20px" }} />
          <div style={{ flex: 1 }}>
            <h3>{item.name}</h3>
            <p>Options: {Object.entries(item.selectedSingle).map(([k, v]) => `${k}: ${v}`).join(", ")}</p>
            {item.selectedToppings.length > 0 && <p>Toppings: {item.selectedToppings.join(", ")}</p>}
            <p>Price: ₹{item.totalPrice}</p>
            <div>
              Quantity: 
<input
  type="number"
  min={1}
  value={item.quantity}
  onChange={(e) => handleQuantityChange(index, Math.max(1, parseInt(e.target.value) || 1))}
  style={{ width: "60px", marginLeft: "10px" }}
/>
              <button onClick={() => handleRemove(index)} style={{ marginLeft: "15px", background: "#ff4b81", color: "white", padding: "5px 10px", borderRadius: "5px", border: "none" }}>Remove</button>
            </div>
          </div>
        </div>
      ))}

      <h3>Total: ₹{total}</h3>
      <button onClick={goToCheckout} style={{ background: "#ff4b81", color: "white", padding: "12px 25px", borderRadius: "8px", border: "none", cursor: "pointer" }}>
        Proceed to Checkout
      </button>
    </div>
  );
};

export default Cart;
