import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, TextField } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

// Convert string/number to array
const toArray = (value) => {
  if (!value) return [];
  if (Array.isArray(value)) return value;
  if (typeof value === "number") return [value];

  if (typeof value === "string") {
    try {
      const parsed = JSON.parse(value);
      return Array.isArray(parsed) ? parsed : [parsed];
    } catch {
      return value.split(",").map((x) => Number(x)).filter(Boolean);
    }
  }
  return [];
};

// Normalize selectedSingle
const normalizeSelectedSingle = (data) => {
  if (!data) return {};

  if (typeof data === "string") {
    try {
      return JSON.parse(data);
    } catch {
      return {};
    }
  }

  if (Array.isArray(data)) {
    return data.reduce((acc, item) => {
      acc[item.type] = item.id;
      return acc;
    }, {});
  }

  return data; // already object
};

export default function CartPage() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    load();
  }, []);

  useEffect(() => {
  console.log("CART DATA:", items);
}, [items]);

  const load = async () => {
    const res = await getCart();
    setItems(res.data);
  };

  const removeItem = async (id) => {
    await removeFromCart(id);
    load();
  };

  const updateQty = async (id, qty) => {
    if (qty < 1) return;
    await updateCartItem(id, { quantity: qty });
    load();
  };

  const totalPrice = items.reduce(
    (sum, it) => sum + (it.totalPrice ?? it.basePrice * it.quantity),
    0
  );

  if (!items.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Box display="grid" gap={2}>
        {items.map((it) => {
          const selected = normalizeSelectedSingle(it.selectedSingle);
          const toppings = toArray(it.selectedToppings);

          return (
            <Paper key={it.id} sx={{ display: "flex", p: 2 }}>
              <img
                src={`/images/${it.image}`}
                alt={it.name}
                style={{ width: 120, borderRadius: 8, marginRight: 16 }}
              />

              <Box sx={{ flex: 1 }}>
                <Typography variant="h6">{it.name}</Typography>

                <Typography variant="body2">
                  <b>Size:</b> {selected.size_label ?? "None"}
                </Typography>

                <Typography variant="body2">
                  <b>Flavor:</b> {selected.flavor_label ?? "None"}
                </Typography>

                <Typography variant="body2">
                  <b>Shape:</b> {selected.shape_label ?? "None"}
                </Typography>

                <Typography variant="body2">
                  <b>Toppings:</b>{" "}
                  {toppings.length
                    ? toppings.map((t) => t.label).join(", ")
                    : "None"}
                </Typography>
              </Box>

              <Box sx={{ textAlign: "right" }}>
                <TextField
                  type="number"
                  value={it.quantity}
                  onChange={(e) =>
                    updateQty(it.id, Math.max(1, Number(e.target.value)))
                  }
                  sx={{ width: 80, mb: 1 }}
                  InputProps={{ inputProps: { min: 1 } }}
                />

                <Typography>
                  ₹{it.totalPrice ?? it.basePrice * it.quantity}
                </Typography>

                <Button color="error" onClick={() => removeItem(it.id)}>
                  Remove
                </Button>
              </Box>
            </Paper>
          );
        })}
      </Box>

      <Typography variant="h5" sx={{ mt: 3 }}>
        Total: ₹{totalPrice}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 2 }}
        onClick={() => navigate("/checkout")}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );
}
